import React, { useRef } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import jsPDF from "jspdf";
import html2canvas from "html2canvas";

export default function Preview() {
  const location = useLocation();
  const navigate = useNavigate();
  const { formData, costs } = location.state || {};

  const invoiceRef = useRef();

  if (!formData || !costs) {
    navigate("/");
    return null;
  }

  const vatTotal = Math.round(costs.totalCost * 1.1);
  const services = [];

  if (formData.includeBase) {
    services.push({ name: "Í±∞ÎûòÍµ¨Ï°∞ Î∞è Í≥ºÏÑ∏ÏûêÎ£å Ï†úÏ∂ú Î≤îÏúÑ Í≤ÄÌÜ†", price: costs.baseCost });
  }
  if (formData.includeReason) {
    services.push({ name: "Í≥ºÏÑ∏ÏûêÎ£å ÎØ∏Ï†úÏ∂ú ÏÇ¨Ïú†ÏÑú ÏûëÏÑ± ÏßÄÏõê", price: costs.reasonCost });
  }
  if (formData.includeDoc) {
    services.push({ name: "Í≥ºÏÑ∏ÏûêÎ£å Ï†úÏ∂ú ÏÑúÎ•ò Ï†ïÎπÑ Î∞è Ï†ÑÏûêÏ†úÏ∂ú ÎåÄÌñâ", price: costs.docCost });
  }
  if (formData.includeReport) {
    services.push({ name: "Í≥ºÏÑ∏Í∞ÄÍ≤© Í≤∞Ï†ï ÏÇ¨Ïú† Î≥¥Í≥†ÏÑú ÏûëÏÑ±", price: costs.reportCost });
  }
  if (formData.includeRelated) {
    services.push({ name: "ÌäπÏàòÍ¥ÄÍ≥ÑÏûê Í±∞Îûò Î∂ÑÏÑù Î∞è Ïù¥Ï†ÑÍ∞ÄÍ≤© Í≤ÄÌÜ† Î¶¨Ìè¨Ìä∏", price: costs.relatedCost });
  }

  const now = new Date();
  const dueDate = new Date(now);
  dueDate.setDate(now.getDate() + 15);

  // PDF ÏÉùÏÑ± Ìï®Ïàò
  const generatePDF = () => {
    const input = invoiceRef.current;
    html2canvas(input, { scale: 2 }).then((canvas) => {
      const imgData = canvas.toDataURL("image/png");
      const pdf = new jsPDF("p", "pt", "a4");

      const imgProps = pdf.getImageProperties(imgData);
      const pdfWidth = pdf.internal.pageSize.getWidth();
      const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;

      pdf.addImage(imgData, "PNG", 0, 0, pdfWidth, pdfHeight);
      pdf.save(`${formData.company}_Í≤¨Ï†ÅÏÑú.pdf`);
    });
  };

  return (
    <div style={{ fontFamily: "'Segoe UI', 'Helvetica Neue', sans-serif", padding: 60, backgroundColor: "#fff", color: "#333", fontSize: 14, lineHeight: 1.6 }}>
      <div ref={invoiceRef}>

        <header style={{ marginBottom: 30 }}>
          <div style={{ fontSize: 14, letterSpacing: "1.5px", color: "#666", textTransform: "uppercase", fontWeight: 500 }}>
            THE HANYOUNG CUSTOMS SERVICES
          </div>
          <h1 style={{ fontSize: 48, fontWeight: 700, letterSpacing: "2px", margin: "5px 0 0", color: "#111" }}>INVOICE</h1>
        </header>

        <table style={{ width: "100%", marginBottom: 20 }}>
          <tbody>
            <tr>
              <td style={{ fontWeight: "bold" }}>ÏàòÏã†</td>
              <td>{formData.company}</td>
              <td style={{ fontWeight: "bold" }}>Î∞úÏã†</td>
              <td>Í¥ÄÏÑ∏Î≤ïÏù∏ ÌïúÏòÅ</td>
            </tr>
            <tr>
              <td style={{ fontWeight: "bold" }}>ÏùºÏûê</td>
              <td>{now.toLocaleDateString("ko-KR")}</td>
              <td style={{ fontWeight: "bold" }}>Î¨∏ÏÑúÎ≤àÌò∏</td>
              <td>{formData.doc_number}</td>
            </tr>
          </tbody>
