import React, { useState, useEffect, useMemo } from "react";
import { useNavigate } from "react-router-dom";

export default function QuotationFTA() {
  const navigate = useNavigate();

  const ftaMatrix = useMemo(() => [
    [30000, 25000, 20000, 15000, 10000, 5000, 3000],
    [null, 30000, 25000, 20000, 15000, 10000, 5000],
    [null, 35000, 30000, 25000, 20000, 15000, 10000],
    [null, 40000, 35000, 30000, 25000, 20000, 15000],
    [null, null, 40000, 35000, 30000, 25000, 20000],
    [null, null, 45000, 40000, 35000, 30000, 25000],
  ], []);

  const originMatrix = useMemo(() => [
    [15000, 12000, 10000, 8000, 5000, 3000, 2000],
    [18000, 15000, 12000, 10000, 8000, 5000, 3000],
    [20000, 18000, 15000, 12000, 10000, 8000, 5000],
    [22000, 20000, 18000, 15000, 12000, 10000, 8000],
  ], []);


  const groupOptions = [
    "10Í∞ú Ïù¥Ìïò",
    "10Í∞ú Ï¥àÍ≥º 30Í∞ú Ïù¥Ìïò",
    "30Í∞ú Ï¥àÍ≥º 60Í∞ú Ïù¥Ìïò",
    "60Í∞ú Ï¥àÍ≥º 100Í∞ú Ïù¥Ìïò",
    "100Í∞ú Ï¥àÍ≥º 200Í∞ú Ïù¥Ìïò",
    "200Í∞ú Ï¥àÍ≥º"
  ];

  const itemOptions = [
    "10Í∞ú Ïù¥Ìïò",
    "10Í∞ú Ï¥àÍ≥º 100Í∞ú Ïù¥Ìïò",
    "100Í∞ú Ï¥àÍ≥º 500Í∞ú Ïù¥Ìïò",
    "500Í∞ú Ï¥àÍ≥º 1000Í∞ú Ïù¥Ìïò",
    "1000Í∞ú Ï¥àÍ≥º 3000Í∞ú Ïù¥Ìïò",
    "3000Í∞ú Ï¥àÍ≥º 10000Í∞ú Ïù¥Ìïò",
    "10000Í∞ú Ï¥àÍ≥º"
  ];

  const originGroupOptions = ["10Í∞ú Ïù¥Ìïò", "30Í∞ú Ïù¥Ìïò", "60Í∞ú Ïù¥Ìïò", "60Í∞ú Ï¥àÍ≥º"];
  const originItemOptions = itemOptions;

  const [form, setForm] = useState({
    company: "",
    doc_number: "",
    groupIndex: 0,
    itemIndex: 0,
    originGroupIndex: 0,
    originItemIndex: 0,
    qtyFTA: 1,
    qtyOrigin: 1,
    discountRate: 0,
    note: "",
    includeFTA: false,
    includeOrigin: false,
  });

  const [costs, setCosts] = useState({
    unitPrice: 0,
    totalAmount: 0,
    discount: 0,
    vat: 0,
    total: 0,
  });

useEffect(() => {
  if (!form.includeFTA && !form.includeOrigin) {
    setCosts({ unitPrice: 0, totalAmount: 0, discount: 0, vat: 0, total: 0 });
    return;
  }

  let raw = 0;
  if (form.includeFTA) {
    const ftaUnit = ftaMatrix[form.groupIndex][form.itemIndex] ?? 0;
    raw += ftaUnit * form.qtyFTA;
  }
  if (form.includeOrigin) {
    const originUnit = originMatrix[form.originGroupIndex][form.originItemIndex] ?? 0;
    raw += originUnit * form.qtyOrigin;
  }

  const discount = Math.round(raw * (form.discountRate / 100));
  const vat = Math.round((raw - discount) * 0.1);
  const total = raw - discount + vat;

  setCosts({
    unitPrice: raw,
    totalAmount: raw,
    discount,
    vat,
    total,
  });
}, [form, ftaMatrix, originMatrix]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]:
        type === "checkbox"
          ? checked
          : name === "qty" || name === "discountRate"
          ? Number(value)
          : value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const selectedData = [];
    if (form.includeFTA) {
      selectedData.push({
        label: "ÌíàÎ™©Î∂ÑÎ•ò",
        detail: `ÌíàÎ™©Íµ∞Ïàò: ${groupOptions[form.groupIndex]}, ÌíàÎ≤àÏàò: ${itemOptions[form.itemIndex]}`,
        qty: form.qtyFTA,
        value: ftaMatrix[form.groupIndex][form.itemIndex] ?? 0,
        note: form.note,
      });
    }
    if (form.includeOrigin) {
      selectedData.push({
        label: "ÏõêÏÇ∞ÏßÄ ÌåêÏ†ï",
        detail: `ÌèâÍ∑† ÏõêÏû¨Î£åÏàò: ${originGroupOptions[form.originGroupIndex]}, ÌíàÎ≤àÏàò: ${originItemOptions[form.originItemIndex]}`,
        qty: form.qtyOrigin,
        value: originMatrix[form.originGroupIndex][form.originItemIndex] ?? 0,
        note: form.note,
      });
    }
    navigate("/FTApreview", {
      state: {
        formData: {
          company: form.company,
          doc_number: form.doc_number,
          discountRate: form.discountRate,
          note: form.note,
        },
        costs: {
          discountAmount: costs.discount,
          vat: costs.vat,
          totalCost: costs.total,
        },
        selectedData,
      },
    });
  };

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: 900, margin: "0 auto", padding: 30, fontFamily: "'Pretendard', sans-serif" }}>
      <h2 style={{ fontSize: 26, fontWeight: 700, color: "#1a2e59", marginBottom: 30 }}>FTA ÌíàÎ™©Î∂ÑÎ•ò Í≤¨Ï†ÅÏÑú</h2>

      {/* ÌöåÏÇ¨Î™Ö & Î¨∏ÏÑúÎ≤àÌò∏ */}
      <div style={{ display: "flex", gap: 20, marginBottom: 30 }}>
        <div style={{ width: "40%" }}>
          <label style={{ fontWeight: 600 }}>ÏóÖÏ≤¥Î™Ö</label>
          <input
            type="text"
            name="company"
            value={form.company}
            onChange={handleChange}
            placeholder="ÏóÖÏ≤¥Î™ÖÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
            style={{ width: "100%", padding: 10, borderRadius: 6, border: "1.5px solid #ccc" }}
            required
          />
        </div>
        <div style={{ width: "40%" }}>
          <label style={{ fontWeight: 600 }}>Î¨∏ÏÑúÎ≤àÌò∏</label>
          <input
            type="text"
            name="doc_number"
            value={form.doc_number}
            onChange={handleChange}
            placeholder="Î¨∏ÏÑúÎ≤àÌò∏ ÏûÖÎ†•"
            style={{ width: "100%", padding: 10, borderRadius: 6, border: "1.5px solid #ccc" }}
            required
          />
        </div>
      </div>

      {/* ÌÖåÏù¥Î∏î */}

      <table style={{ width: "100%", borderCollapse: "collapse", marginBottom: 30 }}>
        <thead>
          <tr style={{ backgroundColor: "#3f72af", color: "#fff" }}>
            <th style={{ padding: 12 }}>‚úî</th>
            <th style={{ padding: 12 }}>Ìï≠Î™©</th>
            <th style={{ padding: 12 }}>ÏÑ∏Î∂ÄÎÇ¥Ïö©</th>
            <th style={{ padding: 12 }}>ÏàòÎüâ</th>
            <th style={{ padding: 12 }}>Í∏àÏï°</th>
          </tr>
        </thead>
        <tbody>
          <tr style={{ borderBottom: "1px solid #ccc" }}>
            <td style={{ padding: 12, textAlign: "center" }}>
              <input
                type="checkbox"
                name="includeFTA"
                checked={form.includeFTA}
                onChange={handleChange}
              />
            </td>
            <td style={{ padding: 12, textAlign: "center" }}>ÌíàÎ™©Î∂ÑÎ•ò</td>
            <td style={{ padding: 12 }}>
              <div style={{ display: "flex", gap: 12, alignItems: "center", flexWrap: "wrap" }}>
                <span>ÌíàÎ™©Íµ∞Ïàò:</span>
                <select name="groupIndex" value={form.groupIndex} onChange={handleChange} style={{ padding: 8, borderRadius: 6 }}>
                  {groupOptions.map((opt, i) => (
                    <option key={i} value={i}>{opt}</option>
                  ))}
                </select>
                <span>ÌíàÎ≤àÏàò:</span>
                <select name="itemIndex" value={form.itemIndex} onChange={handleChange} style={{ padding: 8, borderRadius: 6 }}>
                  {itemOptions.map((opt, i) => (
                    <option key={i} value={i}>{opt}</option>
                  ))}
                </select>
              </div>
            </td>
            <td style={{ padding: 12, textAlign: "center" }}>
              <input type="number" name="qtyFTA" value={form.qtyFTA}
                min={1}
                onChange={handleChange}
                style={{ width: 80, padding: 6, borderRadius: 6, textAlign: "right" }}
              />
            </td>
            <td style={{ padding: 12, textAlign: "right" }}>
              {form.includeFTA ? (ftaMatrix[form.groupIndex][form.itemIndex] * form.qtyFTA).toLocaleString() + " Ïõê" : ""}
            </td>
          </tr>

          <tr style={{ borderBottom: "1px solid #ccc" }}>
            <td style={{ padding: 12, textAlign: "center" }}>
              <input
                type="checkbox"
                name="includeOrigin"
                checked={form.includeOrigin}
                onChange={handleChange}
              />
            </td>
            <td style={{ padding: 12, textAlign: "center" }}>ÏõêÏÇ∞ÏßÄ ÌåêÏ†ï</td>
            <td style={{ padding: 12 }}>
              <div style={{ display: "flex", gap: 12, alignItems: "center", flexWrap: "wrap" }}>
                <span>ÌèâÍ∑† ÏõêÏû¨Î£åÏàò:</span>
                <select name="originGroupIndex" value={form.originGroupIndex} onChange={handleChange} style={{ padding: 8, borderRadius: 6 }}>
                  {originGroupOptions.map((opt, i) => (
                    <option key={i} value={i}>{opt}</option>
                  ))}
                </select>
                <span>ÌíàÎ≤àÏàò:</span>
                <select name="originItemIndex" value={form.originItemIndex} onChange={handleChange} style={{ padding: 8, borderRadius: 6 }}>
                  {originItemOptions.map((opt, i) => (
                    <option key={i} value={i}>{opt}</option>
                  ))}
                </select>
              </div>
            </td>
            <td style={{ padding: 12, textAlign: "center" }}>
              <input type="number" name="qtyOrigin" value={form.qtyOrigin}
                min={1}
                onChange={handleChange}
                style={{ width: 80, padding: 6, borderRadius: 6, textAlign: "right" }}
              />
            </td>
            <td style={{ padding: 12, textAlign: "right" }}>
              {form.includeOrigin ? (originMatrix[form.originGroupIndex][form.originItemIndex] * form.qtyOrigin).toLocaleString() + " Ïõê" : ""}
            </td>
          </tr>

        </tbody>
      </table>

      {/* Ìï†Ïù∏ Î∞è Ìï©Í≥Ñ */}
      <div style={{ textAlign: "right", lineHeight: 2, fontSize: 15, marginBottom: 30 }}>
        <div>
          Ìï†Ïù∏Ïú®:
          <input
            type="number"
            name="discountRate"
            value={form.discountRate}
            onChange={handleChange}
            style={{ width: 60, marginLeft: 8, padding: 6, borderRadius: 6, border: "1px solid #ccc" }}
          />
          % (-{costs.discount.toLocaleString()} Ïõê)
        </div>
        <div>VAT (10%): {costs.vat.toLocaleString()} Ïõê</div>
        <div style={{ fontWeight: 700, fontSize: 16, marginTop: 10 }}>
          ÏµúÏ¢Ö Ìï©Í≥Ñ: {costs.total.toLocaleString()} Ïõê
        </div>
      </div>

      {/* ÌäπÏù¥ÏÇ¨Ìï≠ */}
      <div style={{ marginBottom: 30 }}>
        <label style={{ fontWeight: 600 }}>ÌäπÏù¥ÏÇ¨Ìï≠ / ÎπÑÍ≥†</label>
        <textarea
          name="note"
          rows={4}
          value={form.note}
          onChange={handleChange}
          style={{ width: "100%", borderRadius: 8, border: "1.5px solid #ccc", padding: 12 }}
          placeholder="ÌäπÏù¥ÏÇ¨Ìï≠Ïù¥ ÏûàÎã§Î©¥ ÏûëÏÑ±Ìï¥ Ï£ºÏÑ∏Ïöî"
        />
      </div>

      {/* Î≤ÑÌäº */}
      <div style={{ display: "flex", justifyContent: "center", gap: 20 }}>
        <button
          type="button"
          onClick={() => navigate("/")}
          style={{ backgroundColor: "#aaa", color: "#fff", padding: "12px 30px", borderRadius: 6, border: "none", cursor: "pointer" }}
        >
          Îã§ÏãúÌïòÍ∏∞
        </button>
        <button
          type="submit"
          style={{ backgroundColor: "#3f72af", color: "#fff", padding: "12px 30px", borderRadius: 6, border: "none", cursor: "pointer", fontWeight: 600 }}
        >
          üìÑ Í≤¨Ï†ÅÏÑú ÏÉùÏÑ±
        </button>
      </div>
    </form>
  );
}
