import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import jsPDF from "jspdf";
import html2canvas from "html2canvas";

export default function ConsultingPreview() {
  const location = useLocation();
  const navigate = useNavigate();

  const {
    selectedData = [],
    company = "",
    doc_number = "",
    discountRate = 0,
    vat = 0,
    totalCost = 0,
    discountAmount = 0,
    note = "",
  } = location.state || {};

  const today = new Date();
  const dateString = `${String(today.getFullYear()).slice(2)}${String(
    today.getMonth() + 1
  ).padStart(2, "0")}${String(today.getDate()).padStart(2, "0")}`;

  const downloadPDF = () => {
    const doc = new jsPDF("p", "pt", "a4");
    const input = document.getElementById("invoice-content");

    const safeName = company ? company.replace(/[^Í∞Ä-Ìû£a-zA-Z0-9]/g, "") : "Í≥†Í∞ùÏÇ¨";
    const filename = `[Í¥ÄÏÑ∏Î≤ïÏù∏ÌïúÏòÅ] ${safeName}_Ïª®ÏÑ§ÌåÖ Í≤¨Ï†ÅÏÑú_${dateString}.pdf`;

    html2canvas(input, { scale: 2 }).then((canvas) => {
      const imgData = canvas.toDataURL("image/png");
      const pdfWidth = doc.internal.pageSize.getWidth() - 40;
      const imgProps = doc.getImageProperties(imgData);
      const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;

      doc.addImage(imgData, "PNG", 20, 20, pdfWidth, pdfHeight);
      doc.save(filename);
    });
  };

  return (
    <>
      <div
        id="invoice-content"
        style={{
          fontFamily: "'Pretendard', sans-serif",
          backgroundColor: "#fff",
          color: "#222",
          padding: 60,
          maxWidth: 800,
          margin: "60px auto",
          border: "1px solid #ddd",
          lineHeight: 1.7,
          minHeight: "1120px",
          display: "flex",
          flexDirection: "column",
          justifyContent: "space-between",
        }}
      >
        {/* Header */}
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: 60 }}>
          <div>
            <h1 style={{ fontSize: 40, fontWeight: 700, marginBottom: 10 }}>Invoice</h1>
            <p style={{ fontSize: 14 }}>No. HY25{doc_number || "XXX"} / {today.toLocaleDateString("ko-KR")}</p>
          </div>
          <div style={{ width: 170 }}>
            <img
              src={`${process.env.PUBLIC_URL}/images/HYLOGO_NAVY.png`}
              alt="ÌïúÏòÅ Î°úÍ≥†"
              style={{ width: "100%", objectFit: "contain" }}
            />
          </div>
        </div>

        {/* Client Info */}
        <div style={{ display: "flex", justifyContent: "space-between", marginBottom: 40 }}>
          <div>
            <strong>Client</strong>
            <p>{company || "ÏóÖÏ≤¥Î™Ö ÏóÜÏùå"}</p>
          </div>
          <div style={{ textAlign: "right" }}>
            <strong>Issued By</strong>
            <p>Í¥ÄÏÑ∏Î≤ïÏù∏ ÌïúÏòÅ</p>
          </div>
        </div>

        {/* Table */}
        <table style={{ width: "100%", borderCollapse: "collapse", marginBottom: 40, fontSize: 14 }}>
          <thead>
            <tr style={{ backgroundColor: "#f9f9f9", borderBottom: "1px solid #ccc" }}>
              <th style={{ padding: 10, textAlign: "left" }}>Ìï≠Î™©</th>
              <th style={{ padding: 10, textAlign: "center" }}>ÏàòÎüâ</th>
              <th style={{ padding: 10, textAlign: "center" }}>Îã®Í∞Ä</th>
              <th style={{ padding: 10, textAlign: "right" }}>Í∏àÏï°</th>
              <th style={{ padding: 10 }}>ÎπÑÍ≥†</th>
            </tr>
          </thead>
          <tbody>
            {selectedData.map((item, idx) => (
              <tr key={idx} style={{ borderBottom: "1px solid #eee" }}>
                <td style={{ padding: 10 }}>{item.label}</td>
                <td style={{ padding: 10, textAlign: "center" }}>{item.qty}</td>
                <td style={{ padding: 10, textAlign: "center" }}>{Number(item.unit || 0).toLocaleString()}</td>
                <td style={{ padding: 10, textAlign: "right" }}>{Number(item.value || 0).toLocaleString()}</td>
                <td style={{ padding: 10 }}>{item.note}</td>
              </tr>
            ))}
          </tbody>
        </table>

        {/* Summary */}
        <div style={{ textAlign: "right", fontSize: 15, lineHeight: 2 }}>
          {discountRate > 0 && (
            <p>Ìï†Ïù∏Ïú®: {discountRate}% ({Number(discountAmount || 0).toLocaleString()} Ïõê)</p>
          )}
          <p>Î∂ÄÍ∞ÄÏÑ∏ (VAT 10%): {Number(vat || 0).toLocaleString()} Ïõê</p>
          <h3 style={{ fontSize: 20, marginTop: 10 }}>Ï¥ù Ìï©Í≥Ñ: {Number(totalCost || 0).toLocaleString()} Ïõê</h3>
        </div>

        {/* Footer */}
        <div style={{ display: "flex", justifyContent: "space-between", marginTop: 60, fontSize: 13 }}>
          <div>
            <p><strong>Bank:</strong> Ïã†ÌïúÏùÄÌñâ</p>
            <p><strong>Account:</strong> 140-015-193246</p>
            <p><strong>Holder:</strong> Í¥ÄÏÑ∏Î≤ïÏù∏ ÌïúÏòÅ Ïù∏Ï≤úÏßÄÏÇ¨</p>
          </div>
          <div style={{ textAlign: "right", color: "#666" }}>
            <p><strong>Valid Until:</strong> {new Date(today.getTime() + 15 * 86400000).toLocaleDateString("ko-KR")}</p>
            <p>Ïù∏Ï≤ú ÏÑúÍµ¨ Ïù¥Ïùå4Î°ú 6, KRÎ≤ïÏ°∞ÌÉÄÏõå 915Ìò∏</p>
            <p>TEL: 032-713-4897 / FAX: 032-713-4898</p>
          </div>
        </div>
      </div>

      {/* Buttons */}
      <div style={{ marginTop: 40, display: "flex", justifyContent: "center", gap: 20 }}>
        <button onClick={() => navigate("/consulting")} style={{ backgroundColor: "#aaa", border: "none", padding: "10px 30px", borderRadius: 6, color: "#fff", fontWeight: 600 }}>Îã§ÏãúÌïòÍ∏∞</button>
        <button onClick={downloadPDF} style={{ backgroundColor: "#111", border: "none", padding: "10px 30px", borderRadius: 6, color: "#fff", fontWeight: 700 }}>üì• PDF Îã§Ïö¥Î°úÎìú</button>
      </div>
    </>
  );
}
