import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const baseMatrix = [
  [300000, 400000, 500000, 600000, 700000],
  [400000, 500000, 600000, 700000, 800000],
  [600000, 700000, 800000, 900000, 1000000],
  [800000, 900000, 1000000, 1100000, 1200000],
];

const relatedMatrix = [
  [3000000, 4000000, 5000000, 6000000, 7000000],
  [4000000, 5000000, 6000000, 7000000, 8000000],
  [5000000, 6000000, 7000000, 8000000, 9000000],
  [6000000, 7000000, 8000000, 9000000, 10000000],
];

export default function QuoteForm() {
  const navigate = useNavigate();

  const [state, setState] = useState({
    company: "",
    doc_number: "",
    includeBase: false,
    partners: 10,
    importAmount: 1,
    includeReason: false,
    reasonType: "basic",
    missingItems: 0,
    includeDoc: false,
    docPartners: 10,
    includeReport: false,
    reportItems: {
      royalty: false,
      productionSupport: false,
      commission: false,
      freightInsurance: false,
      packaging: false,
      postBenefit: false,
      indirectPayment: false,
    },
    includeRelated: false,
    relatedPartners: 10,
    relatedImportAmount: 1,
  });

  const [costs, setCosts] = useState({
    baseCost: 0,
    reasonCost: 0,
    docCost: 0,
    reportCost: 0,
    relatedCost: 0,
    totalCost: 0,
  });

  const handleChange = (e) => {
    const { name, type, checked, value } = e.target;

    if (type === "checkbox" && name.startsWith("reportItems")) {
      const key = name.split(".")[1];
      setState((prev) => ({
        ...prev,
        reportItems: {
          ...prev.reportItems,
          [key]: checked,
        },
      }));
    } else if (type === "checkbox") {
      setState((prev) => ({
        ...prev,
        [name]: checked,
      }));
    } else {
      setState((prev) => ({
        ...prev,
        [name]: type === "number" ? Number(value) : value,
      }));
    }
  };

  const calculateCosts = () => {
    let total = 0;

    // Base cost Í≥ÑÏÇ∞
    let baseCost = 0;
    if (state.includeBase) {
      let row =
        state.partners <= 10
          ? 0
          : state.partners <= 30
          ? 1
          : state.partners <= 100
          ? 2
          : 3;
      baseCost = baseMatrix[row][state.importAmount - 1];
      total += baseCost;
    }

    // Reason cost Í≥ÑÏÇ∞
    let reasonCost = 0;
    if (state.includeReason) {
      reasonCost =
        state.reasonType === "basic"
          ? 100000
          : 100000 + state.missingItems * 30000;
      total += reasonCost;
    }

    // Doc cost Í≥ÑÏÇ∞
    let docCost = 0;
    if (state.includeDoc) {
      docCost = state.docPartners * 50000;
      total += docCost;
    }

    // Report cost Í≥ÑÏÇ∞
    let reportCost = 0;
    if (state.includeReport) {
      const checkedCount = Object.values(state.reportItems).filter(Boolean)
        .length;
      reportCost = checkedCount * 1000000;
      total += reportCost;
    }

    // Related cost Í≥ÑÏÇ∞
    let relatedCost = 0;
    if (state.includeRelated) {
      let row =
        state.relatedPartners <= 10
          ? 0
          : state.relatedPartners <= 30
          ? 1
          : state.relatedPartners <= 50
          ? 2
          : 3;
      relatedCost = relatedMatrix[row][state.relatedImportAmount - 1];
      total += relatedCost;
    }

    setCosts({ baseCost, reasonCost, docCost, reportCost, relatedCost, totalCost: total });
  };

  // Ïà®Í≤®ÏßÑ input ÏÉùÏÑ± Ìï®Ïàò
  const renderHiddenInputs = () => {
    const inputs = [];

    if (state.includeBase) {
      inputs.push(
        <input key="service-base" type="hidden" name="service" value="Í±∞ÎûòÍµ¨Ï°∞ Î∞è Í≥ºÏÑ∏ÏûêÎ£å Ï†úÏ∂ú Î≤îÏúÑ Í≤ÄÌÜ†" />
      );
      inputs.push(
        <input key="price-base" type="hidden" name="price" value={costs.baseCost} />
      );
    }
    if (state.includeReason) {
      inputs.push(
        <input key="service-reason" type="hidden" name="service" value="Í≥ºÏÑ∏ÏûêÎ£å ÎØ∏Ï†úÏ∂ú ÏÇ¨Ïú†ÏÑú ÏûëÏÑ± ÏßÄÏõê" />
      );
      inputs.push(
        <input key="price-reason" type="hidden" name="price" value={costs.reasonCost} />
      );
    }
    if (state.includeDoc) {
      inputs.push(
        <input key="service-doc" type="hidden" name="service" value="Í≥ºÏÑ∏ÏûêÎ£å Ï†úÏ∂ú ÏÑúÎ•ò Ï†ïÎπÑ Î∞è Ï†ÑÏûêÏ†úÏ∂ú ÎåÄÌñâ" />
      );
      inputs.push(
        <input key="price-doc" type="hidden" name="price" value={costs.docCost} />
      );
    }
    if (state.includeReport) {
      inputs.push(
        <input key="service-report" type="hidden" name="service" value="Í≥ºÏÑ∏Í∞ÄÍ≤© Í≤∞Ï†ï ÏÇ¨Ïú† Î≥¥Í≥†ÏÑú ÏûëÏÑ±" />
      );
      inputs.push(
        <input key="price-report" type="hidden" name="price" value={costs.reportCost} />
      );
    }
    if (state.includeRelated) {
      inputs.push(
        <input key="service-related" type="hidden" name="service" value="ÌäπÏàòÍ¥ÄÍ≥ÑÏûê Í±∞Îûò Î∂ÑÏÑù Î∞è Ïù¥Ï†ÑÍ∞ÄÍ≤© Í≤ÄÌÜ† Î¶¨Ìè¨Ìä∏" />
      );
      inputs.push(
        <input key="price-related" type="hidden" name="price" value={costs.relatedCost} />
      );
    }

    return inputs;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    calculateCosts();
    // ÎπÑÏö© Í≥ÑÏÇ∞ ÌõÑ preview ÌéòÏù¥ÏßÄÎ°ú Îç∞Ïù¥ÌÑ∞ ÎÑòÍ∏∞Í∏∞
    navigate("/preview", { state: { formData: state, costs: costs } });
  };

  return (
    <form
      method="post"
      onSubmit={handleSubmit}
      style={{
        fontFamily: "'Pretendard', 'Segoe UI', 'Helvetica Neue', sans-serif",
        backgroundColor: "#fafbfc",
        color: "#222",
        padding: 40,
        maxWidth: 900,
        margin: "0 auto",
        lineHeight: 1.6,
      }}
    >
      <h1
        style={{
          textAlign: "center",
          fontWeight: 700,
          fontSize: 32,
          marginBottom: 40,
          color: "#1a2e59",
          letterSpacing: "0.02em",
        }}
      >
        Í≥ºÏÑ∏ÏûêÎ£å Ï†úÏ∂ú Ïª®ÏÑ§ÌåÖ Í≤¨Ï†ÅÏÑú ÏÉùÏÑ±Í∏∞
      </h1>

      {/* ÌöåÏÇ¨Î™Ö */}
      <div
        className="form-group"
        style={{
          marginBottom: 30,
          maxWidth: 400,
          marginLeft: "auto",
          marginRight: "auto",
          display: "flex",
          flexDirection: "column",
        }}
      >
        <label
          htmlFor="company"
          style={{ fontWeight: 600, marginBottom: 10, color: "#1a2e59" }}
        >
          ÌöåÏÇ¨Î™Ö:
        </label>
        <input
          type="text"
          id="company"
          name="company"
          required
          placeholder="ÌöåÏÇ¨Î™ÖÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
          value={state.company}
          onChange={handleChange}
          style={{
            padding: 12,
            fontSize: 15,
            borderRadius: 8,
            border: "2px solid #ced4da",
            boxSizing: "border-box",
          }}
        />
      </div>

      {/* Î¨∏ÏÑúÎ≤àÌò∏ */}
      <div
        className="form-group"
        style={{
          marginBottom: 30,
          maxWidth: 400,
          marginLeft: "auto",
          marginRight: "auto",
          display: "flex",
          flexDirection: "column",
        }}
      >
        <label
          htmlFor="doc_number"
          style={{ fontWeight: 600, marginBottom: 10, color: "#1a2e59" }}
        >
          Î¨∏ÏÑúÎ≤àÌò∏:
        </label>
        <input
          type="text"
          id="doc_number"
          name="doc_number"
          required
          placeholder="Ïà´Ïûê 3ÏûêÎ¶¨ ÏûÖÎ†•"
          value={state.doc_number}
          onChange={handleChange}
          style={{
            padding: 12,
            fontSize: 15,
            borderRadius: 8,
            border: "2px solid #ced4da",
            boxSizing: "border-box",
          }}
        />
      </div>

      {/* ÏÑúÎπÑÏä§ Ìï≠Î™© */}
      <h2
        style={{
          textAlign: "center",
          margin: "50px 0 30px",
          fontSize: 22,
          fontWeight: 700,
          color: "#1a2e59",
          borderBottom: "3px solid #3f72af",
          paddingBottom: 10,
          maxWidth: 420,
          marginLeft: "auto",
          marginRight: "auto",
        }}
      >
        ÏÑúÎπÑÏä§ Ìï≠Î™© ÏÑ†ÌÉù
      </h2>

      <table
        style={{
          width: "100%",
          borderCollapse: "separate",
          borderSpacing: "0 12px",
          fontSize: 15,
          color: "#333",
          marginBottom: 50,
        }}
      >
        <thead>
          <tr
            style={{
              backgroundColor: "#3f72af",
              color: "white",
              fontWeight: 700,
              fontSize: 16,
              letterSpacing: "0.04em",
            }}
          >
            <th
              style={{
                borderRadius: "10px 0 0 10px",
                padding: "16px 20px",
                textAlign: "center",
              }}
            >
              ‚úî Ìè¨Ìï®
            </th>
            <th style={{ padding: "16px 20px" }}>Ìï≠Î™©</th>
            <th style={{ padding: "16px 20px" }}>ÏÑ†ÌÉù / ÏàòÎüâ</th>
            <th style={{ padding: "16px 20px", textAlign: "center" }}>Îã®Í∞Ä</th>
            <th
              style={{
                borderRadius: "0 10px 10px 0",
                padding: "16px 20px",
                textAlign: "right",
              }}
            >
              Ìï©Í≥Ñ
            </th>
          </tr>
        </thead>

        <tbody>
          {/* Í±∞ÎûòÍµ¨Ï°∞ Î∞è Í≥ºÏÑ∏ÏûêÎ£å Ï†úÏ∂ú Î≤îÏúÑ Í≤ÄÌÜ† */}
          <tr>
            <td
              style={{
                backgroundColor: "white",
                padding: "16px 20px",
                borderRadius: "10px 0 0 10px",
                textAlign: "center",
              }}
            >
              <input
                type="checkbox"
                name="includeBase"
                checked={state.includeBase}
                onChange={handleChange}
              />
            </td>
            <td style={{ backgroundColor: "white", padding: "16px 20px" }}>
              <b>Í±∞ÎûòÍµ¨Ï°∞ Î∞è Í≥ºÏÑ∏ÏûêÎ£å Ï†úÏ∂ú Î≤îÏúÑ Í≤ÄÌÜ†</b>
            </td>
            <td
              style={{
                backgroundColor: "white",
                padding: "16px 20px",
                textAlign: "left",
              }}
            >
              Í±∞ÎûòÏ≤ò Ïàò:{" "}
              <input
                type="number"
                name="partners"
                min="0"
                value={state.partners}
                onChange={handleChange}
                style={{
                  width: 70,
                  marginLeft: 10,
                  borderRadius: 7,
                  border: "1.8px solid #ced4da",
                  padding: 8,
                }}
              />
              <br />
              ÏàòÏûÖÍ∏àÏï°:
              <select
                name="importAmount"
                value={state.importAmount}
                onChange={handleChange}
                style={{
                  width: 130,
                  marginLeft: 10,
                  borderRadius: 8,
                  border: "1.8px solid #ced4da",
                  padding: 8,
                }}
              >
                <option value={1}>50Ïñµ ÎØ∏Îßå</option>
                <option value={2}>50~100Ïñµ</option>
                <option value={3}>100~200Ïñµ</option>
                <option value={4}>200~500Ïñµ</option>
                <option value={5}>500Ïñµ Ïù¥ÏÉÅ</option>
              </select>
            </td>
            <td
              style={{
                backgroundColor: "white",
                padding: "16px 20px",
                textAlign: "center",
              }}
            >
              -
            </td>
            <td
              style={{
                backgroundColor: "white",
                padding: "16px 20px",
                textAlign: "right",
              }}
            >
              {costs.baseCost.toLocaleString()}
            </td>
          </tr>

          {/* Í≥ºÏÑ∏ÏûêÎ£å ÎØ∏Ï†úÏ∂ú ÏÇ¨Ïú†ÏÑú ÏûëÏÑ± ÏßÄÏõê */}
          <tr>
            <td
              style={{
                backgroundColor: "white",
                padding: "16px 20px",
                textAlign: "center",
              }}
            >
              <input
                type="checkbox"
                name="includeReason"
                checked={state.includeReason}
                onChange={handleChange}
              />
            </td>
            <td style={{ backgroundColor: "white", padding: "16px 20px" }}>
              <b>Í≥ºÏÑ∏ÏûêÎ£å ÎØ∏Ï†úÏ∂ú ÏÇ¨Ïú†ÏÑú ÏûëÏÑ± ÏßÄÏõê</b>
            </td>
            <td
              style={{
                backgroundColor: "white",
                padding: "16px 20px",
                textAlign: "left",
              }}
            >
              <label>
                <input
                  type="radio"
                  name="reasonType"
                  value="basic"
                  checked={state.reasonType === "basic"}
                  onChange={handleChange}
                />{" "}
                Í±∞Îûò ÏÇ¨Ïã§ ÏóÜÏùå
              </label>
              <br />
              <label>
                <input
                  type="radio"
                  name="reasonType"
                  value="missing"
                  checked={state.reasonType === "missing"}
                  onChange={handleChange}
                />{" "}
                Í±∞Îûò ÏûàÏùå + ÏûêÎ£å ÎØ∏Ï†úÏ∂ú
                <br />
                Ìï≠Î™© Ïàò:{" "}
                <input
                  type="number"
                  name="missingItems"
                  min="0"
                  value={state.missingItems}
                  onChange={handleChange}
                  style={{
                    width: 70,
                    marginLeft: 10,
                    borderRadius: 7,
                    border: "1.8px solid #ced4da",
                    padding: 8,
                  }}
                />
              </label>
            </td>
            <td
              style={{
                backgroundColor: "white",
                padding: "16px 20px",
                textAlign: "center",
              }}
            >
              Í∏∞Î≥∏ 100,000 + Ìï≠Î™©Îãπ 30,000
            </td>
            <td
              style={{
                backgroundColor: "white",
                padding: "16px 20px",
                textAlign: "right",
              }}
            >
              {costs.reasonCost.toLocaleString()}
            </td>
          </tr>

          {/* Í≥ºÏÑ∏ÏûêÎ£å Ï†úÏ∂ú ÏÑúÎ•ò Ï†ïÎπÑ Î∞è Ï†ÑÏûêÏ†úÏ∂ú ÎåÄÌñâ */}
          <tr>
            <td
              style={{
                backgroundColor: "white",
                padding: "16px 20px",
                textAlign: "center",
              }}
            >
              <input
                type="checkbox"
                name="includeDoc"
                checked={state.includeDoc}
                onChange={handleChange}
              />
            </td>
            <td style={{ backgroundColor: "white", padding: "16px 20px" }}>
              <b>Í≥ºÏÑ∏ÏûêÎ£å Ï†úÏ∂ú ÏÑúÎ•ò Ï†ïÎπÑ Î∞è Ï†ÑÏûêÏ†úÏ∂ú ÎåÄÌñâ</b>
            </td>
            <td
              style={{
                backgroundColor: "white",
                padding: "16px 20px",
                textAlign: "left",
              }}
            >
              Í±∞ÎûòÏ≤ò Ïàò:{" "}
              <input
                type="number"
                name="docPartners"
                min="0"
                value={state.docPartners}
                onChange={handleChange}
                style={{
                  width: 70,
                  marginLeft: 10,
                  borderRadius: 7,
                  border: "1.8px solid #ced4da",
                  padding: 8,
                }}
              />
            </td>
            <td
              style={{
                backgroundColor: "white",
                padding: "16px 20px",
                textAlign: "center",
              }}
            >
              50,000Ïõê
            </td>
            <td
              style={{
                backgroundColor: "white",
                padding: "16px 20px",
                textAlign: "right",
              }}
            >
              {costs.docCost.toLocaleString()}
            </td>
          </tr>

          {/* Í≥ºÏÑ∏Í∞ÄÍ≤© Í≤∞Ï†ï ÏÇ¨Ïú† Î≥¥Í≥†ÏÑú ÏûëÏÑ± */}
          <tr>
            <td
              style={{
                backgroundColor: "white",
                padding: "16px 20px",
                textAlign: "center",
              }}
            >
              <input
                type="checkbox"
                name="includeReport"
                checked={state.includeReport}
                onChange={handleChange}
              />
            </td>
            <td style={{ backgroundColor: "white", padding: "16px 20px" }}>
              <b>
                Í≥ºÏÑ∏Í∞ÄÍ≤© Í≤∞Ï†ï ÏÇ¨Ïú† Î≥¥Í≥†ÏÑú ÏûëÏÑ±
                <br />
                <small>(Î°úÏó¥Ìã∞, ÏÉùÏÇ∞ÏßÄÏõêÎπÑ Îì± Ìï¥Îãπ Ìï≠Î™©Î≥Ñ)</small>
              </b>
            </td>
            <td
              style={{
                backgroundColor: "white",
                padding: "16px 20px",
                textAlign: "left",
              }}
            >
              <label>
                <input
                  type="checkbox"
                  name="reportItems.royalty"
                  checked={state.reportItems.royalty}
                  onChange={handleChange}
                />{" "}
                Î°úÏó¥Ìã∞
              </label>
              <br />
              <label>
                <input
                  type="checkbox"
                  name="reportItems.productionSupport"
                  checked={state.reportItems.productionSupport}
                  onChange={handleChange}
                />{" "}
                ÏÉùÏÇ∞ÏßÄÏõêÎπÑ
              </label>
              <br />
              <label>
                <input
                  type="checkbox"
                  name="reportItems.commission"
                  checked={state.reportItems.commission}
                  onChange={handleChange}
                />{" "}
                ÏàòÏàòÎ£å/Ï§ëÍ∞úÎ£å
              </label>
              <br />
              <label>
                <input
                  type="checkbox"
                  name="reportItems.freightInsurance"
                  checked={state.reportItems.freightInsurance}
                  onChange={handleChange}
                />{" "}
                Ïö¥ÏûÑ/Î≥¥ÌóòÎ£å
              </label>
              <br />
              <label>
                <input
                  type="checkbox"
                  name="reportItems.packaging"
                  checked={state.reportItems.packaging}
                  onChange={handleChange}
                />{" "}
                Ïö©Í∏∞/Ìè¨Ïû•ÎπÑ
              </label>
              <br />
              <label>
                <input
                  type="checkbox"
                  name="reportItems.postBenefit"
                  checked={state.reportItems.postBenefit}
                  onChange={handleChange}
                />{" "}
                ÏÇ¨ÌõÑÍ∑ÄÏÜçÏù¥Ïùµ
              </label>
              <br />
              <label>
                <input
                  type="checkbox"
                  name="reportItems.indirectPayment"
                  checked={state.reportItems.indirectPayment}
                  onChange={handleChange}
                />{" "}
                Í∞ÑÏ†ëÏßÄÍ∏âÍ∏àÏï°
              </label>
            </td>
            <td
              style={{
                backgroundColor: "white",
                padding: "16px 20px",
                textAlign: "center",
              }}
            >
              1,000,000Ïõê/Ìï≠Î™©
            </td>
            <td
              style={{
                backgroundColor: "white",
                padding: "16px 20px",
                textAlign: "right",
              }}
            >
              {costs.reportCost.toLocaleString()}
            </td>
          </tr>

          {/* ÌäπÏàòÍ¥ÄÍ≥ÑÏûê Í±∞Îûò Î∂ÑÏÑù Î∞è Ïù¥Ï†ÑÍ∞ÄÍ≤© Í≤ÄÌÜ† Î¶¨Ìè¨Ìä∏ */}
          <tr>
            <td
              style={{
                backgroundColor: "white",
                padding: "16px 20px",
                textAlign: "center",
              }}
            >
              <input
                type="checkbox"
                name="includeRelated"
                checked={state.includeRelated}
                onChange={handleChange}
              />
            </td>
            <td style={{ backgroundColor: "white", padding: "16px 20px" }}>
              <b>ÌäπÏàòÍ¥ÄÍ≥ÑÏûê Í±∞Îûò Î∂ÑÏÑù Î∞è Ïù¥Ï†ÑÍ∞ÄÍ≤© Í≤ÄÌÜ† Î¶¨Ìè¨Ìä∏</b>
            </td>
            <td
              style={{
                backgroundColor: "white",
                padding: "16px 20px",
                textAlign: "left",
              }}
            >
              Í±∞ÎûòÏ≤ò Ïàò:{" "}
              <input
                type="number"
                name="relatedPartners"
                min="0"
                value={state.relatedPartners}
                onChange={handleChange}
                style={{
                  width: 70,
                  marginLeft: 10,
                  borderRadius: 7,
                  border: "1.8px solid #ced4da",
                  padding: 8,
                }}
              />
              <br />
              ÏàòÏûÖÍ∏àÏï°:
              <select
                name="relatedImportAmount"
                value={state.relatedImportAmount}
                onChange={handleChange}
                style={{
                  width: 130,
                  marginLeft: 10,
                  borderRadius: 8,
                  border: "1.8px solid #ced4da",
                  padding: 8,
                }}
              >
                <option value={1}>50Ïñµ ÎØ∏Îßå</option>
                <option value={2}>50~100Ïñµ</option>
                <option value={3}>100~200Ïñµ</option>
                <option value={4}>200~500Ïñµ</option>
                <option value={5}>500Ïñµ Ïù¥ÏÉÅ</option>
              </select>
            </td>
            <td
              style={{
                backgroundColor: "white",
                padding: "16px 20px",
                textAlign: "center",
              }}
            >
              -
            </td>
            <td
              style={{
                backgroundColor: "white",
                padding: "16px 20px",
                textAlign: "right",
              }}
            >
              {costs.relatedCost.toLocaleString()}
            </td>
          </tr>
        </tbody>

        <tfoot>
          <tr>
            <th
              colSpan="4"
              style={{
                padding: "16px 20px",
                textAlign: "right",
                borderRadius: "0 0 0 10px",
              }}
            >
              Ï¥ù Ìï©Í≥Ñ
            </th>
            <th
              style={{
                padding: "16px 20px",
                textAlign: "right",
                borderRadius: "0 0 10px 0",
              }}
            >
              {costs.totalCost.toLocaleString()}
            </th>
          </tr>
        </tfoot>
      </table>

      {/* Ïà®Í≤®ÏßÑ inputÎì§ */}
      {renderHiddenInputs()}

      <button type="button" onClick={calculateCosts} className="submit-btn">
        üí∞ Í≤¨Ï†Å Í≥ÑÏÇ∞ÌïòÍ∏∞
      </button>
      <button
        type="submit"
        className="submit-btn"
