import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function QuotationConsulting() {
  const navigate = useNavigate();

  const [form, setForm] = useState({
    company: "",
    doc_number: "",
    items: [
      { id: 1, label: "", qty: "", unit: "", amount: 0, note: "" },
    ],
  });

  const handleChange = (e, id, field) => {
    const value = e.target.value;
    const updatedItems = form.items.map((item) => {
      if (item.id === id) {
        const newItem = { ...item, [field]: value };
        const qty = parseFloat(newItem.qty) || 0;
        const unit = parseFloat(newItem.unit) || 0;
        newItem.amount = qty * unit;
        return newItem;
      }
      return item;
    });
    setForm({ ...form, items: updatedItems });
  };

  const handleBasicChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const addRow = () => {
    const newId = form.items.length ? form.items[form.items.length - 1].id + 1 : 1;
    setForm({
      ...form,
      items: [
        ...form.items,
        { id: newId, label: "", qty: "", unit: "", amount: 0, note: "" },
      ],
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const selectedData = form.items.filter(item => item.label || item.amount).map(item => ({
      label: item.label,
      qty: item.qty,
      unit: item.unit,
      value: item.amount,
      note: item.note,
    }));

    navigate("/preview", {
      state: {
        selectedData,
        company: form.company,
        doc_number: form.doc_number,
      },
    });
  };

  const total = form.items.reduce((sum, item) => {
    return sum + (Number(item.amount) || 0);
  }, 0);

  return (
    <form
      onSubmit={handleSubmit}
      style={{
        maxWidth: 900,
        margin: "0 auto",
        padding: 30,
        fontFamily: "'Pretendard', sans-serif",
        backgroundColor: "#f9f9f9",
        borderRadius: 8,
      }}
    >
      {/* Ìó§Îçî */}
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: 30 }}>
        <div style={{ width: 180 }}>
          <img
            src={`${process.env.PUBLIC_URL}/images/HYLOGO_NAVY.png`}
            alt="ÌïúÏòÅ Î°úÍ≥†"
            style={{ width: "100%", height: "auto", objectFit: "contain" }}
          />
        </div>
        <h2 style={{ fontSize: 26, fontWeight: 700, color: "#1a2e59" }}>Ïª®ÏÑ§ÌåÖ Í≤¨Ï†ÅÏÑú</h2>
      </div>

      {/* ÏóÖÏ≤¥Î™Ö, Î¨∏ÏÑúÎ≤àÌò∏ */}
      <div style={{ display: "flex", gap: 20, marginBottom: 40 }}>
        <div style={{ width: "50%" }}>
          <label style={{ display: "block", fontWeight: 600, marginBottom: 6 }}>ÏóÖÏ≤¥Î™Ö</label>
          <input
            type="text"
            name="company"
            value={form.company}
            onChange={handleBasicChange}
            placeholder="ÏóÖÏ≤¥Î™ÖÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
            style={{ width: "100%", padding: 10, borderRadius: 6, border: "1.5px solid #ccc" }}
            required
          />
        </div>
        <div style={{ width: "45%" }}>
          <label style={{ display: "block", fontWeight: 600, marginBottom: 6 }}>Î¨∏ÏÑúÎ≤àÌò∏</label>
          <input
            type="text"
            name="doc_number"
            value={form.doc_number}
            onChange={handleBasicChange}
            placeholder="Î¨∏ÏÑúÎ≤àÌò∏ ÏûÖÎ†•"
            style={{ width: "100%", padding: 10, borderRadius: 6, border: "1.5px solid #ccc" }}
            required
          />
        </div>
      </div>

      {/* ÌÖåÏù¥Î∏î */}
      <table style={{ width: "100%", borderCollapse: "collapse", marginBottom: 20 }}>
        <thead>
          <tr style={{ backgroundColor: "#3f72af", color: "#fff" }}>
    <th style={{ padding: 12, width: "40%" }}>Ìï≠Î™©</th>     {/* ‚¨Ö Ìï≠Î™© Îçî ÎÑìÍ≤å */}
    <th style={{ padding: 12, width: "10%" }}>ÏàòÎüâ</th>
    <th style={{ padding: 12, width: "15%" }}>Îã®Í∞Ä</th>
    <th style={{ padding: 12, width: "10%" }}>Í∏àÏï°</th>
    <th style={{ padding: 12, width: "25%" }}>ÎπÑÍ≥†</th>  
          </tr>
        </thead>
        <tbody>
          {form.items.map((item) => (
            <tr key={item.id} style={{ borderBottom: "1px solid #ddd" }}>
              <td style={{ padding: 10 }}>
                <input
                  type="text"
                  value={item.label}
                  onChange={(e) => handleChange(e, item.id, "label")}
                  placeholder="Ìï≠Î™© ÏûÖÎ†•"
                  style={{ width: "100%", padding: 6}}
                />
              </td>
              <td style={{ padding: 10 }}>
                <input
                  type="number"
                  value={item.qty}
                  onChange={(e) => handleChange(e, item.id, "qty")}
                  placeholder="ÏàòÎüâ"
                  style={{ width: "100%", padding: 6, textAlign: "right" }}
                />
              </td>
              <td style={{ padding: 10 }}>
                <input
                  type="number"
                  value={item.unit}
                  onChange={(e) => handleChange(e, item.id, "unit")}
                  placeholder="Îã®Í∞Ä"
                  style={{ width: "60%", padding: 6, textAlign: "right" }}
                />
              </td>
              <td style={{ padding: 10, textAlign: "right" }}>
                {item.amount.toLocaleString()} Ïõê
              </td>
              <td style={{ padding: 10 }}>
                <input
                  type="text"
                  value={item.note}
                  onChange={(e) => handleChange(e, item.id, "note")}
                  placeholder="ÎπÑÍ≥† ÏûÖÎ†•"
                  style={{ width: "100%", padding: 6 }}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Ï∂îÍ∞Ä Î≤ÑÌäº */}
      <div style={{ textAlign: "left", marginBottom: 30 }}>
        <button
          type="button"
          onClick={addRow}
          style={{
            backgroundColor: "#1a2e59",
            color: "#fff",
            padding: "6px 14px",
            borderRadius: 6,
            border: "none",
            fontWeight: 600,
            cursor: "pointer",
          }}
        >
          ‚ûï Ìï≠Î™© Ï∂îÍ∞Ä
        </button>
      </div>

      {/* Ï¥ù Ìï©Í≥Ñ */}
      <div style={{ textAlign: "right", fontSize: 16, marginBottom: 30 }}>
        <strong>Ï¥ù Ìï©Í≥Ñ: {total.toLocaleString()} Ïõê</strong>
      </div>

      {/* Ï†úÏ∂ú */}
      <div style={{ textAlign: "center" }}>
        <button
          type="submit"
          style={{
            backgroundColor: "#3f72af",
            color: "#fff",
            border: "none",
            borderRadius: 6,
            padding: "12px 30px",
            fontSize: 16,
            fontWeight: 600,
            cursor: "pointer",
          }}
        >
          üìÑ Í≤¨Ï†ÅÏÑú ÏÉùÏÑ±
        </button>
      </div>
    </form>
  );
}
