// src/QuotationCustoms.js
import { useNavigate } from "react-router-dom";
import React, { useState } from "react";
import HY_BRANCHES from "./hyBranches"; // Ï∂îÍ∞Ä

export default function QuotationCustoms() {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    branchKey: "IC", // ÏßÄÏÇ¨ Í∏∞Î≥∏Í∞í
    company: "",
    doc_number: "",
    feeRate: "0.2",
    minFee: "30000",
    maxFee: "",
    correctionFee: "20000",
    ftaApplication: "30000",
    requirementApplication: "",
    exportFeeRate: "0.15",
    exportMinFee: "15000",
    exportMaxFee: "",
    exportCorrectionFee: "10000",
    exportFtaCertificateAuthorized: "50000",
    exportFtaCertificateUnauth: "100000",
    notes: {
      feeRate: "",
      minFee: "",
      maxFee: "",
      correctionFee: "",
      ftaApplication: "",
      requirementApplication: "",
      exportFeeRate: "",
      exportMinFee: "",
      exportMaxFee: "",
      exportCorrectionFee: "Ìè¨Ïû• ÏàòÎüâ, Ï§ëÎüâÏóê Í¥ÄÌïú Ï†ïÏ†ï Ï†úÏô∏",
      exportFtaCertificateAuthorized: "",
      exportFtaCertificateUnauth: "",
    },
    checked: {
      feeRate: true,
      minFee: true,
      maxFee: false,
      correctionFee: true,
      ftaApplication: false,
      requirementApplication: false,
      exportFeeRate: true,
      exportMinFee: true,
      exportMaxFee: false,
      exportCorrectionFee: true,
      exportFtaCertificateAuthorized: false,
      exportFtaCertificateUnauth: false,
    },
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (type === "checkbox") {
      setForm((prev) => ({
        ...prev,
        checked: {
          ...prev.checked,
          [name]: checked,
        },
      }));
    } else if (name.startsWith("note_")) {
      const key = name.replace("note_", "");
      setForm((prev) => ({
        ...prev,
        notes: {
          ...prev.notes,
          [key]: value,
        },
      }));
    } else {
      setForm((prev) => ({
        ...prev,
        [name]: value,
      }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const selectedItems = Object.keys(form.checked).filter((k) => form.checked[k]);
    const selectedData = selectedItems.map((item) => ({
      label: item,
      value: form[item],
      note: form.notes[item],
    }));
    navigate("/customspreview", {
      state: {
        selectedData,
        branchKey: form.branchKey,
        company: form.company,
        doc_number: form.doc_number,
      },
    });
  };

  const renderTable = (title, items) => (
    <>
      <h2 style={{ textAlign: "center", fontSize: 20, fontWeight: 700, marginTop: 40 }}>{title}</h2>
      <table style={{ width: "100%", borderCollapse: "collapse", marginBottom: 20 }}>
        <thead>
          <tr style={{ backgroundColor: "#3f72af", color: "white" }}>
            <th style={{ padding: 12 }}>‚úî</th>
            <th style={{ padding: 12 }}>Ìï≠Î™©</th>
            <th style={{ padding: 12 }}>Í∏àÏï°</th>
            <th style={{ padding: 12 }}>ÎπÑÍ≥†</th>
          </tr>
        </thead>
        <tbody>
          {items.map(({ key, label, suffix }) => (
            <tr key={key} style={{ backgroundColor: "#fff", borderBottom: "1px solid #ddd" }}>
              <td style={{ textAlign: "center" }}>
                <input type="checkbox" name={key} checked={form.checked[key]} onChange={handleChange} />
              </td>
              <td style={{ padding: 10 }}>{label}</td>
              <td style={{ padding: 10 }}>
                <input
                  type="text"
                  name={key}
                  value={form[key]}
                  onChange={handleChange}
                  placeholder={`Ïòà: ${suffix === "%" ? "0.15" : "30000"}`}
                  style={{ width: 100, padding: 6, marginRight: 6 }}
                />
                {suffix}
              </td>
              <td style={{ padding: 10 }}>
                <input
                  type="text"
                  name={`note_${key}`}
                  value={form.notes[key]}
                  onChange={handleChange}
                  placeholder="ÎπÑÍ≥† ÏûÖÎ†•"
                  style={{ width: "100%", padding: 6 }}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );

  return (
    <form
      onSubmit={handleSubmit}
      style={{
        maxWidth: 900,
        margin: "0 auto",
        padding: 30,
        fontFamily: "'Pretendard', sans-serif",
        backgroundColor: "#f9f9f9",
        borderRadius: 8,
      }}
    >
      {/* Î°úÍ≥† + ÌÉÄÏù¥ÌãÄ */}
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: 30 }}>
        <div style={{ width: 180 }}>
          <img
            src={`${process.env.PUBLIC_URL}/images/HYLOGO_NAVY.png`}
            alt="ÌïúÏòÅ Î°úÍ≥†"
            style={{ width: "100%", height: "auto", objectFit: "contain" }}
          />
        </div>
        <h2 style={{ fontSize: 26, fontWeight: 700, color: "#1a2e59" }}>ÌÜµÍ¥Ä ÏàòÏàòÎ£å Í≤¨Ï†ÅÏÑú</h2>
      </div>

      {/* ÏßÄÏÇ¨ ÏÑ†ÌÉù ÎùºÎîîÏò§ */}
      <div style={{ maxWidth: 900, margin: "0 auto 20px", padding: "12px 0" }}>
        <div style={{ fontWeight: 600, color: "#1a2e59", marginBottom: 8 }}>Î≥∏ÏßÄÏÇ¨ ÏÑ†ÌÉù</div>
        <div style={{ display: "flex", gap: 16, flexWrap: "wrap" }}>
          {Object.values(HY_BRANCHES).map((b) => (
            <label key={b.key} style={{ display: "flex", alignItems: "center", gap: 6 }}>
              <input
                type="radio"
                name="branchKey"
                value={b.key}
                checked={form.branchKey === b.key}
                onChange={handleChange}
              />
              {b.label}
            </label>
          ))}
        </div>
      </div>

      {/* ÏóÖÏ≤¥Î™Ö & Î¨∏ÏÑúÎ≤àÌò∏ */}
      <div style={{ display: "flex", flexWrap: "wrap", gap: 20, marginBottom: 40 }}>
        <div style={{ flex: 1, minWidth: 200 }}>
          <label style={{ display: "block", fontWeight: 600, marginBottom: 6 }}>ÏóÖÏ≤¥Î™Ö</label>
          <input
            type="text"
            name="company"
            value={form.company}
            onChange={handleChange}
            placeholder="ÏóÖÏ≤¥Î™ÖÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
            style={{ width: "80%", padding: 10, borderRadius: 6, border: "1.5px solid #ccc" }}
            required
          />
        </div>
        <div style={{ flex: 1, minWidth: 200 }}>
          <label style={{ display: "block", fontWeight: 600, marginBottom: 6 }}>Î¨∏ÏÑúÎ≤àÌò∏</label>
          <input
            type="text"
            name="doc_number"
            value={form.doc_number}
            onChange={handleChange}
            placeholder="Î¨∏ÏÑúÎ≤àÌò∏ ÏûÖÎ†•"
            style={{ width: "80%", padding: 10, borderRadius: 6, border: "1.5px solid #ccc" }}
            required
          />
        </div>
      </div>

      {renderTable("ÏàòÏûÖÌÜµÍ¥Ä ÏàòÏàòÎ£å", [
        { key: "feeRate", label: "ÏàòÏàòÎ£å ÏöîÏú®", suffix: "%" },
        { key: "minFee", label: "MIN", suffix: "Ïõê" },
        { key: "maxFee", label: "MAX", suffix: "Ïõê" },
        { key: "correctionFee", label: "Ïã†Í≥†Ï†ïÏ†ï", suffix: "Ïõê" },
        { key: "ftaApplication", label: "FTA ÌòëÏ†ïÏã†Ï≤≠", suffix: "Ïõê" },
        { key: "requirementApplication", label: "ÏöîÍ±¥Ïã†Ï≤≠", suffix: "Ïõê" },
      ])}

      {renderTable("ÏàòÏ∂úÌÜµÍ¥Ä ÏàòÏàòÎ£å", [
        { key: "exportFeeRate", label: "ÏàòÏàòÎ£å ÏöîÏú®", suffix: "%" },
        { key: "exportMinFee", label: "MIN", suffix: "Ïõê" },
        { key: "exportMaxFee", label: "MAX", suffix: "Ïõê" },
        { key: "exportCorrectionFee", label: "Ïã†Í≥†Ï†ïÏ†ï", suffix: "Ïõê" },
        { key: "exportFtaCertificateAuthorized", label: "FTA C/O Î∞úÍ∏â (Ïù∏Ï¶ùÏàòÏ∂úÏûê)", suffix: "Ïõê" },
        { key: "exportFtaCertificateUnauth", label: "FTA C/O Î∞úÍ∏â (ÎπÑÏù∏Ï¶ùÏàòÏ∂úÏûê)", suffix: "Ïõê" },
      ])}

      <div style={{ display: "flex", justifyContent: "center", gap: 20, marginTop: 30 }}>
        <button
          onClick={() => navigate("/")}
          style={{
            backgroundColor: "#aaa",
            border: "none",
            padding: "12px 30px",
            borderRadius: 6,
            cursor: "pointer",
            fontWeight: 600,
            fontSize: 16,
            color: "#fff",
          }}
        >
          Îã§ÏãúÌïòÍ∏∞
        </button>
        <button
          type="submit"
          style={{
            backgroundColor: "#3f72af",
            color: "#fff",
            border: "none",
            borderRadius: 6,
            padding: "12px 30px",
            fontSize: 16,
            fontWeight: 600,
            cursor: "pointer",
          }}
        >
          üìÑ Í≤¨Ï†ÅÏÑú ÏÉùÏÑ±
        </button>
      </div>
    </form>
  );
}
