// src/customspreview.js
import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import jsPDF from "jspdf";
import html2canvas from "html2canvas";
import { HY_BRANCHES, formatDocNo } from "./hyBranches";

export default function CustomsPreview() {
  const location = useLocation();
  const navigate = useNavigate();

  const { selectedData, company, doc_number, branchKey } = location.state || {};
  const today = new Date();

  // ÏßÄÏÇ¨ Í∞ùÏ≤¥ (ÏóÜÏúºÎ©¥ Ï≤´ ÏßÄÏÇ¨Î°ú Ìè¥Î∞±)
  const branch = (branchKey && HY_BRANCHES[branchKey]) || Object.values(HY_BRANCHES)[0] || {};

  const dateString = `${String(today.getFullYear()).slice(2)}${String(today.getMonth() + 1).padStart(2, "0")}${String(
    today.getDate()
  ).padStart(2, "0")}`;

  const downloadPDF = () => {
    const doc = new jsPDF("p", "pt", "a4");
    const input = document.getElementById("invoice-content");

    const companyNameSafe = company?.replace(/[^Í∞Ä-Ìû£a-zA-Z0-9\s]/g, "") || "ÏóÖÏ≤¥Î™ÖÏóÜÏùå";
    const filename = `[Í¥ÄÏÑ∏Î≤ïÏù∏ÌïúÏòÅ] ${companyNameSafe} ÌÜµÍ¥Ä ÏàòÏàòÎ£å Í≤¨Ï†ÅÏÑú_${dateString}.pdf`;

    html2canvas(input, { scale: 1.2 }).then((canvas) => {
      const imgData = canvas.toDataURL("image/png");
      const pdfWidth = doc.internal.pageSize.getWidth() - 40;
      const imgProps = doc.getImageProperties(imgData);
      const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;

      doc.addImage(imgData, "JPEG", 20, 20, pdfWidth, pdfHeight);
      doc.save(filename);
    });
  };

  const labelMap = {
    feeRate: "ÏàòÏàòÎ£å ÏöîÏú®",
    minFee: "MIN",
    maxFee: "MAX",
    correctionFee: "Ïã†Í≥†Ï†ïÏ†ï",
    ftaApplication: "FTA ÌòëÏ†ïÏã†Ï≤≠",
    requirementApplication: "ÏöîÍ±¥Ïã†Ï≤≠",
    exportFeeRate: "ÏàòÏàòÎ£å ÏöîÏú®",
    exportMinFee: "MIN",
    exportMaxFee: "MAX",
    exportCorrectionFee: "Ïã†Í≥†Ï†ïÏ†ï",
    exportFtaCertificateAuthorized: "FTA C/O Î∞úÍ∏â (Ïù∏Ï¶ùÏàòÏ∂úÏûê)",
    exportFtaCertificateUnauth: "FTA C/O Î∞úÍ∏â (ÎπÑÏù∏Ï¶ùÏàòÏ∂úÏûê)",
  };

  const importItems = selectedData?.filter((item) => !item.label.startsWith("export")) || [];
  const exportItems = selectedData?.filter((item) => item.label.startsWith("export")) || [];

  const renderTable = (title, items) => (
    <div style={{ marginTop: 30, marginBottom: 30 }}>
      <h3 style={{ fontSize: 18, marginBottom: 10, color: "#1a2e59" }}>{title}</h3>
      <table style={{ width: "100%", borderCollapse: "collapse", fontSize: 12 }}>
        <thead>
          <tr style={{ borderBottom: "1px solid #ccc", backgroundColor: "#f9f9f9" }}>
            <th style={{ padding: "10px 6px", width: "35%", textAlign: "center" }}>Ìï≠Î™©</th>
            <th style={{ padding: "10px 6px", width: "25%", textAlign: "center" }}>Í∏àÏï°</th>
            <th style={{ padding: "10px 6px", width: "40%", textAlign: "center" }}>ÎπÑÍ≥†</th>
          </tr>
        </thead>
        <tbody>
          {items.map((item, idx) => (
            <tr key={idx} style={{ borderBottom: "1px solid #eee" }}>
              <td style={{ padding: "10px 6px", textAlign: "center", backgroundColor: "#f9f9f9", fontWeight: 500 }}>
                {labelMap[item.label] || item.label}
              </td>
              <td style={{ padding: "10px 6px", textAlign: "center" }}>
                {item.label.includes("Rate") ? `${item.value}%` : `${Number(item.value).toLocaleString()} Ïõê`}
              </td>
              <td style={{ padding: "10px 6px", textAlign: "center" }}>{item.note || "-"}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  return (
    <>
      <div
        id="invoice-content"
        style={{
          fontFamily: "'Pretendard', 'Helvetica Neue', sans-serif",
          backgroundColor: "#fff",
          color: "#222",
          padding: 60,
          paddingTop: 30,
          maxWidth: 800,
          margin: "45px auto",
          lineHeight: 1.7,
          border: "1px solid #ddd",
          minHeight: "1122px",
          boxSizing: "border-box",
          display: "flex",
          flexDirection: "column",
          justifyContent: "space-between",
        }}
      >
        {/* Ìó§Îçî */}
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: 60 }}>
          <div>
            <h1 style={{ fontSize: 32, fontWeight: 700, marginBottom: 10 }}>Quotation</h1>
            <p style={{ fontSize: 14, color: "#555" }}>
              {/* ÏßÄÏÇ¨ ÏΩîÎìú Ìè¨Ìï® Î¨∏ÏÑúÎ≤àÌò∏ */}
              No. {formatDocNo(branch?.codePrefix || "HY", doc_number)} / {today.toLocaleDateString("ko-KR")}
            </p>
          </div>
          <div style={{ width: 170 }}>
            <img
              src={`${process.env.PUBLIC_URL}/images/HYLOGO_NAVY.png`}
              alt="ÌïúÏòÅ Î°úÍ≥†"
              style={{ width: "100%", height: "auto", objectFit: "contain" }}
            />
          </div>
        </div>

        {/* Í≥†Í∞ù/Î∞úÌñâÏûê(ÏßÄÏÇ¨) */}
        <div style={{ display: "flex", justifyContent: "space-between", marginBottom: 0 }}>
          <div>
            <strong>Client</strong>
            <p style={{ fontSize: 14 }}>{company || "Client Name"}</p>
          </div>
          <div style={{ textAlign: "right" }}>
            <strong>Issued By</strong>
            <p style={{ fontSize: 14 }}>
              {/* Í¥ÄÏÑ∏Î≤ïÏù∏ ÌïúÏòÅ (Î≥∏ÏÇ¨/Ïù∏Ï≤ú/‚Ä¶) */}
              Í¥ÄÏÑ∏Î≤ïÏù∏ ÌïúÏòÅ {branch?.label ? `(${branch.label})` : ""}
            </p>
          </div>
        </div>

        {/* ÏàòÏûÖ/ÏàòÏ∂ú ÌÖåÏù¥Î∏î */}
        <div style={{ marginTop: 25 }}>
          {importItems.length > 0 && renderTable("ÏàòÏûÖÌÜµÍ¥Ä ÏàòÏàòÎ£å", importItems)}
          {exportItems.length > 0 && renderTable("ÏàòÏ∂úÌÜµÍ¥Ä ÏàòÏàòÎ£å", exportItems)}
        </div>

        <div style={{ textAlign: "right", fontSize: 14, fontWeight: 600, marginBottom: 40 }}>‚Äª V.A.T Î≥ÑÎèÑ</div>

        {/* Ï¢åÌïòÎã®: ÏßÄÏÇ¨ ÌëúÍ∏∞(Í≥ÑÏ¢å Ï†úÏô∏) + Ïö∞Ï∏° Ïú†Ìö®Í∏∞Í∞Ñ */}
        <div style={{ display: "flex", justifyContent: "space-between", fontSize: 13, color: "#666" }}>
          <div>
            {/* holder / address / TEL/FAX */}
            <p>
              <strong>{branch?.holder || "Í¥ÄÏÑ∏Î≤ïÏù∏ ÌïúÏòÅ"}</strong>
            </p>
            {branch?.address && <p>{branch.address}</p>}
            {(branch?.tel || branch?.fax) && (
              <p>
                {branch.tel ? `TEL: ${branch.tel}` : ""}
                {branch.tel && branch.fax ? " / " : ""}
