import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import jsPDF from "jspdf";
import html2canvas from "html2canvas";

export default function Preview() {
  const location = useLocation();
  const navigate = useNavigate();

  const formData = location.state?.formData || {};
  const costs = location.state?.costs || {};

  const today = new Date();
  const dateString = `${String(today.getFullYear()).slice(2)}${String(
    today.getMonth() + 1
  ).padStart(2, "0")}${String(today.getDate()).padStart(2, "0")}`;

  const downloadPDF = () => {
    const doc = new jsPDF("p", "pt", "a4");
    const input = document.getElementById("invoice-content");

    const companyNameSafe = formData.company
      ? formData.company.replace(/[^가-힣a-zA-Z0-9\s]/g, "")
      : "업체명없음";
    const filename = `[관세법인한영] ${companyNameSafe} 과세자료 컨설팅 견적서_${dateString}.pdf`;

    html2canvas(input, { scale: 2 }).then((canvas) => {
      const imgData = canvas.toDataURL("image/png");
      const pdfWidth = doc.internal.pageSize.getWidth() - 40;
      const imgProps = doc.getImageProperties(imgData);
      const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;

      doc.addImage(imgData, "PNG", 20, 20, pdfWidth, pdfHeight);
      doc.save(filename);
    });
  };

  const serviceItems = [
    {
      label: "거래구조 및 과세자료 제출 범위 검토",
      checked: formData.includeBase,
      quantity: formData.partners || 1,
      total: costs.baseCost,
      details: `연 수입금액: ${formData.importAmount}단계`,
    },
    {
      label: "과세자료 미제출 사유서 작성 지원",
      checked: formData.includeReason,
      quantity: formData.reasonPartners || 1,
      total: costs.reasonCost,
      details:
        formData.reasonType === "basic"
          ? "거래 사실 없음"
          : `거래 있음 + 자료 미제출 (항목 수: ${formData.missingItems || 0})`,
    },
    {
      label: "과세자료 제출 서류 정비 및 전자제출 대행",
      checked: formData.includeDoc,
      quantity: formData.docPartners || 1,
      total: costs.docCost,
      details: `거래처 수: ${formData.docPartners || 0}`,
    },
    {
      label: "과세가격 결정 사유 보고서 작성",
      checked: formData.includeReport,
      quantity:
        Object.values(formData.reportItems || {}).filter(Boolean).length *
        (formData.reportPartners || 1),
      total: costs.reportCost,
      details: `${Object.entries(formData.reportItems || {})
        .filter(([_, v]) => v)
        .map(([k]) => k)
        .join(", ") || "-"}`,
    },
    {
      label: "특수관계자 거래 분석 및 이전가격 검토 리포트",
      checked: formData.includeRelated,
      quantity: formData.relatedPartners || 1,
      total: costs.relatedCost,
      details: `연 수입금액: ${formData.relatedImportAmount}단계`,
    },
  ];

  const selectedServices = serviceItems.filter((item) => item.checked);

  return (
    <div
      id="invoice-content"
      style={{
        fontFamily: "'Helvetica Neue', sans-serif",
        maxWidth: 800,
        margin: "40px auto",
        padding: "40px 60px",
        backgroundColor: "#fff",
        color: "#222",
        fontSize: 14,
        lineHeight: 1.5,
      }}
    >
      <h1 style={{ fontSize: 40, fontWeight: 700 }}>Invoice</h1>
      <p style={{ marginTop: -10 }}>no. {formData.doc_number || "XXXX"}</p>
      <p>{today.toLocaleDateString("ko-KR")}</p>

      <div style={{ display: "flex", justifyContent: "space-between", margin: "30px 0" }}>
        <div>
          <p><strong>Client Name</strong><br />{formData.company}<br />대한민국</p>
        </div>
        <div style={{ textAlign: "right" }}>
          <p><strong>관세법인 한영</strong><br />인천 서구 이음4로6<br />KR법조타워 915호<br />대한민국</p>
        </div>
      </div>

      <table style={{ width: "100%", borderCollapse: "collapse", marginTop: 20, marginBottom: 30 }}>
        <thead>
          <tr>
            <th style={{ borderBottom: "1px solid #ccc", textAlign: "left", paddingBottom: 10 }}>Description</th>
            <th style={{ borderBottom: "1px solid #ccc", textAlign: "center", paddingBottom: 10 }}>Qty</th>
            <th style={{ borderBottom: "1px solid #ccc", textAlign: "right", paddingBottom: 10 }}>Rate</th>
          </tr>
        </thead>
        <tbody>
          {selectedServices.map((item, idx) => (
            <tr key={idx}>
              <td style={{ padding: "10px 0" }}>
                {item.label}<br />
                <span style={{ fontSize: 12, color: "#666" }}>{item.details}</span>
              </td>
              <td style={{ textAlign: "center" }}>{item.quantity}</td>
              <td style={{ textAlign: "right" }}>{item.total.toLocaleString()}원</td>
            </tr>
          ))}
        </tbody>
      </table>

      <div style={{ marginBottom: 30 }}>
        <strong>특이사항</strong>
        <p style={{ marginTop: 6, whiteSpace: "pre-line" }}>{formData.note || "-"}</p>
      </div>

      <div style={{ display: "flex", justifyContent: "flex-end", marginBottom: 30 }}>
        <table style={{ fontSize: 14, textAlign: "right" }}>
          <tbody>
            <tr>
              <td style={{ padding: "4px 8px" }}>Amount Due</td>
              <td style={{ padding: "4px 8px" }}>{(costs.subTotal || 0).toLocaleString()} 원</td>
            </tr>
            <tr>
              <td style={{ padding: "4px 8px" }}>Discount ({formData.discountRate || 0}%)</td>
              <td style={{ padding: "4px 8px" }}>- {costs.discountAmount?.toLocaleString() || 0} 원</td>
            </tr>
            <tr>
              <td style={{ padding: "4px 8px" }}>V.A.T (10%)</td>
              <td style={{ padding: "4px 8px" }}>{costs.vat?.toLocaleString()} 원</td>
            </tr>
            <tr>
              <td style={{ padding: "4px 8px", fontWeight: 700 }}>Total Amount Due</td>
              <td style={{ padding: "4px 8px", fontWeight: 700 }}>{costs.totalCost?.toLocaleString()} 원</td>
            </tr>
          </tbody>
        </table>
      </div>

      <div style={{ display: "flex", justifyContent: "space-between", fontSize: 13, marginTop: 40 }}>
        <div>
          <strong>Payment Details</strong>
          <p>
            Pay via Bank Transfer:<br />
            신한은행<br />
            계좌번호: 140-015-193246<br />
            예금주: 관세법인 한영 인천지사
          </p>
        </div>
        <div style={{ textAlign: "right" }}>
          <strong>Thank you!</strong>
          <p>
            It's been a pleasure working with you.<br />
            Please feel free to reach out with any questions.
          </p>
        </div>
      </div>
    </div>
  );
}
