import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const baseMatrix = [
  [300000, 400000, 500000, 600000, 700000],
  [400000, 500000, 600000, 700000, 800000],
  [600000, 700000, 800000, 900000, 1000000],
  [800000, 900000, 1000000, 1100000, 1200000],
];

const relatedMatrix = [
  [3000000, 4000000, 5000000, 6000000, 7000000],
  [4000000, 5000000, 6000000, 7000000, 8000000],
  [5000000, 6000000, 7000000, 8000000, 9000000],
  [6000000, 7000000, 8000000, 9000000, 10000000],
];

export default function QuoteForm() {
  const navigate = useNavigate();

  const [state, setState] = useState({
    company: "",
    doc_number: "",
    includeBase: false,
    partners: 0,
    importAmount: 1,
    includeReason: false,
    reasonType: "basic",
    missingItems: 0,
    includeDoc: false,
    docPartners: 10,
    includeReport: false,
    reportItems: {
      royalty: false,
      productionSupport: false,
      commission: false,
      freightInsurance: false,
      packaging: false,
      postBenefit: false,
      indirectPayment: false,
    },
    includeRelated: false,
    relatedPartners: 10,
    relatedImportAmount: 1,
    note: "", // 특이사항 상태 추가
  });

  const [costs, setCosts] = useState({
    baseCost: 0,
    reasonCost: 0,
    docCost: 0,
    reportCost: 0,
    relatedCost: 0,
    subTotal: 0, // 합산 (세금 제외)
    vat: 0,      // 10% 부가세
    totalCost: 0,
  });

  const handleChange = (e) => {
    const { name, type, checked, value } = e.target;

    if (type === "checkbox" && name.startsWith("reportItems")) {
      const key = name.split(".")[1];
      setState((prev) => ({
        ...prev,
        reportItems: {
          ...prev.reportItems,
          [key]: checked,
        },
      }));
    } else if (type === "checkbox") {
      setState((prev) => ({
        ...prev,
        [name]: checked,
      }));
    } else {
      setState((prev) => ({
        ...prev,
        [name]: type === "number" ? Number(value) : value,
      }));
    }
  };

  // 특이사항(비고) 입력 처리
  const handleNoteChange = (e) => {
    setState((prev) => ({
      ...prev,
      note: e.target.value,
    }));
  };

  const calculateCosts = () => {
    let subTotal = 0;

    let baseCost = 0;
    if (state.includeBase) {
      let row =
        state.partners <= 10
          ? 0
          : state.partners <= 30
          ? 1
          : state.partners <= 100
          ? 2
          : 3;
      baseCost = baseMatrix[row][state.importAmount - 1];
      subTotal += baseCost;
    }

    let reasonCost = 0;
    if (state.includeReason) {
      reasonCost =
        state.reasonType === "basic"
          ? 100000
          : 100000 + state.missingItems * 30000;
      subTotal += reasonCost;
    }

    let docCost = 0;
    if (state.includeDoc) {
      docCost = state.docPartners * 50000;
      subTotal += docCost;
    }

    let reportCost = 0;
    if (state.includeReport) {
      const checkedCount = Object.values(state.reportItems).filter(Boolean)
        .length;
      reportCost = checkedCount * 1000000;
      subTotal += reportCost;
    }

    let relatedCost = 0;
    if (state.includeRelated) {
      let row =
        state.relatedPartners <= 10
          ? 0
          : state.relatedPartners <= 30
          ? 1
          : state.relatedPartners <= 50
          ? 2
          : 3;
      relatedCost = relatedMatrix[row][state.relatedImportAmount - 1];
      subTotal += relatedCost;
    }

    const vat = Math.round(subTotal * 0.1);
    const totalCost = subTotal + vat;

    setCosts({ baseCost, reasonCost, docCost, reportCost, relatedCost, subTotal, vat, totalCost });
  };

  // 숨겨진 input 생성 함수 (기존 유지)
  const renderHiddenInputs = () => {
    const inputs = [];

    if (state.includeBase) {
      inputs.push(
        <input key="service-base" type="hidden" name="service" value="거래구조 및 과세자료 제출 범위 검토" />
      );
      inputs.push(
        <input key="price-base" type="hidden" name="price" value={costs.baseCost} />
      );
    }
    if (state.includeReason) {
      inputs.push(
        <input key="service-reason" type="hidden" name="service" value="과세자료 미제출 사유서 작성 지원" />
      );
      inputs.push(
        <input key="price-reason" type="hidden" name="price" value={costs.reasonCost} />
      );
    }
    if (state.includeDoc) {
      inputs.push(
        <input key="service-doc" type="hidden" name="service" value="과세자료 제출 서류 정비 및 전자제출 대행" />
      );
      inputs.push(
        <input key="price-doc" type="hidden" name="price" value={costs.docCost} />
      );
    }
    if (state.includeReport) {
      inputs.push(
        <input key="service-report" type="hidden" name="service" value="과세가격 결정 사유 보고서 작성" />
      );
      inputs.push(
        <input key="price-report" type="hidden" name="price" value={costs.reportCost} />
      );
    }
    if (state.includeRelated) {
      inputs.push(
        <input key="service-related" type="hidden" name="service" value="특수관계자 거래 분석 및 이전가격 검토 리포트" />
      );
      inputs.push(
        <input key="price-related" type="hidden" name="price" value={costs.relatedCost} />
      );
    }

    // 특이사항 숨겨진 input 추가
    inputs.push(
      <input key="note" type="hidden" name="note" value={state.note || ""} />
    );

    return inputs;
  };

  // 폼 제출 핸들러 (견적서 생성 버튼 클릭시)
  const handleSubmit = (e) => {
    e.preventDefault();
    calculateCosts();
    // 비용이 계산되는 setCosts가 비동기라서,
    // 바로 navigate하면 이전 비용 상태가 넘어갈 수 있으므로, 
    // calculateCosts 후 navigate는 비용을 직접 계산해서 넘김으로 처리
    let subTotal = 0;
    let baseCost = 0;
    if(state.includeBase){
      let row = state.partners <= 10 ? 0 : state.partners <= 30 ? 1 : state.partners <= 100 ? 2 : 3;
      baseCost = baseMatrix[row][state.importAmount - 1];
      subTotal += baseCost;
    }

    let reasonCost = 0;
    if(state.includeReason){
      reasonCost = state.reasonType === "basic" ? 100000 : 100000 + state.missingItems * 30000;
      subTotal += reasonCost;
    }

    let docCost = 0;
    if(state.includeDoc){
      docCost = state.docPartners * 50000;
      subTotal += docCost;
    }

    let reportCost = 0;
    if(state.includeReport){
      const checkedCount = Object.values(state.reportItems).filter(Boolean).length;
      reportCost = checkedCount * 1000000;
      subTotal += reportCost;
    }

    let relatedCost = 0;
    if(state.includeRelated){
      let row = state.relatedPartners <= 10 ? 0 : state.relatedPartners <= 30 ? 1 : state.relatedPartners <= 50 ? 2 : 3;
      relatedCost = relatedMatrix[row][state.relatedImportAmount - 1];
      subTotal += relatedCost;
    }

    const vat = Math.round(subTotal * 0.1);
    const totalCost = subTotal + vat;

    // 새 페이지로 이동
    navigate("/preview", {
      state: {
        formData: state,
        costs: { baseCost, reasonCost, docCost, reportCost, relatedCost, subTotal, vat, totalCost }
      }
    });
  };

  return (
    <form
      method="post"
      onSubmit={handleSubmit}
      style={{
        fontFamily: "'Pretendard', 'Segoe UI', 'Helvetica Neue', sans-serif",
        backgroundColor: "#fafbfc",
        color: "#222",
        padding: 40,
        maxWidth: 900,
        margin: "0 auto",
        lineHeight: 1.6,
      }}
    >
      <h1
        style={{
          textAlign: "center",
          fontWeight: 700,
          fontSize: 32,
          marginBottom: 40,
          color: "#1a2e59",
          letterSpacing: "0.02em",
        }}
      >
        과세자료 제출 컨설팅 견적서 생성기
      </h1>

      {/* 회사명 */}
      <div
        className="form-group"
        style={{
          marginBottom: 30,
          maxWidth: 400,
          marginLeft: "auto",
          marginRight: "auto",
          display: "flex",
          flexDirection: "column",
        }}
      >
        <label
          htmlFor="company"
          style={{ fontWeight: 600, marginBottom: 10, color: "#1a2e59" }}
        >
          회사명:
        </label>
        <input
          type="text"
          id="company"
          name="company"
          required
          placeholder="회사명을 입력하세요"
          value={state.company}
          onChange={handleChange}
          style={{
            padding: 12,
            fontSize: 15,
            borderRadius: 8,
            border: "2px solid #ced4da",
            boxSizing: "border-box",
          }}
        />
      </div>

      {/* 문서번호 */}
      <div
        className="form-group"
        style={{
          marginBottom: 30,
          maxWidth: 400,
          marginLeft: "auto",
          marginRight: "auto",
          display: "flex",
          flexDirection: "column",
        }}
      >
        <label
          htmlFor="doc_number"
          style={{ fontWeight: 600, marginBottom: 10, color: "#1a2e59" }}
        >
          문서번호:
        </label>
        <input
          type="text"
          id="doc_number"
          name="doc_number"
          required
          placeholder="숫자 3자리 입력"
          value={state.doc_number}
          onChange={handleChange}
          style={{
            padding: 12,
            fontSize: 15,
            borderRadius: 8,
            border: "2px solid #ced4da",
            boxSizing: "border-box",
          }}
        />
      </div>

      {/* 서비스 항목 */}
      <h2
        style={{
          textAlign: "center",
          margin: "50px 0 30px",
          fontSize: 22,
          fontWeight: 700,
          color: "#1a2e59",
          borderBottom: "3px solid #3f72af",
          paddingBottom: 10,
          maxWidth: 420,
          marginLeft: "auto",
          marginRight: "auto",
        }}
      >
        서비스 항목 선택
      </h2>

      {/* ...생략... */}
<table
  style={{
    width: "100%",
    borderCollapse: "separate",
    borderSpacing: "0 12px",
    fontSize: 15,
    color: "#333",
    marginBottom: 20,
  }}
>
  <thead>
    <tr
      style={{
        backgroundColor: "#3f72af",
        color: "white",
        fontWeight: 700,
        fontSize: 16,
        letterSpacing: "0.04em",
      }}
    >
      <th
        style={{
          borderRadius: "10px 0 0 10px",
          padding: "16px 20px",
          textAlign: "center",
        }}
      >
        ✔
      </th>
      <th style={{ padding: "16px 20px" }}>항목</th>
      <th style={{ padding: "16px 20px" }}>세부내용</th>
      <th style={{ padding: "16px 20px", textAlign: "center" }}>
        거래처 수(수량)
      </th>
      <th style={{ padding: "16px 20px", textAlign: "center" }}>단가</th>
      <th
        style={{
          borderRadius: "0 10px 10px 0",
          padding: "16px 20px",
          textAlign: "right",
        }}
      >
        합계
      </th>
    </tr>
  </thead>
  <tbody>
   <tr>
  <td
    style={{
      backgroundColor: "white",
      padding: "16px 20px",
      borderRadius: "10px 0 0 10px",
      textAlign: "center",
    }}
  >
    <input
      type="checkbox"
      name="includeBase"
      checked={state.includeBase}
      onChange={handleChange}
    />
  </td>
  <td style={{ backgroundColor: "white", padding: "16px 20px" }}>
    <b>거래구조 및 과세자료 제출 범위 검토</b>
  </td>
  <td style={{ backgroundColor: "white", padding: "16px 20px" }}>
    {/* 세부내용: 수입금액 드롭다운 */}
    <select
      name="importAmount"
      value={state.importAmount}
      onChange={handleChange}
      style={{
        width: 130,
        borderRadius: 8,
        border: "1.8px solid #ced4da",
        padding: 8,
      }}
    >
      <option value={1}>50억 미만</option>
      <option value={2}>50~100억</option>
      <option value={3}>100~200억</option>
      <option value={4}>200~500억</option>
      <option value={5}>500억 이상</option>
    </select>
  </td>
  <td
    style={{
      backgroundColor: "white",
      padding: "16px 20px",
      textAlign: "center",
    }}
  >
    {/* 거래처 수 입력란, 기본 0 */}
    <input
      type="number"
      name="partners"
      min="0"
      value={state.partners}
      onChange={handleChange}
      style={{
        width: 70,
        borderRadius: 7,
        border: "1.8px solid #ced4da",
        padding: 8,
      }}
    />
  </td>
  <td
    style={{
      backgroundColor: "white",
      padding: "16px 20px",
      textAlign: "center",
    }}
  >
    -
  </td>
  <td
    style={{
      backgroundColor: "white",
      padding: "16px 20px",
      textAlign: "right",
    }}
  >
    {costs.baseCost.toLocaleString()}
  </td>
</tr>


<tr>
  <td
    style={{
      backgroundColor: "white",
      padding: "16px 20px",
      textAlign: "center",
    }}
  >
    <input
      type="checkbox"
      name="includeReason"
      checked={state.includeReason}
      onChange={handleChange}
    />
  </td>
  <td style={{ backgroundColor: "white", padding: "16px 20px" }}>
    <b>과세자료 미제출 사유서 작성 지원</b>
  </td>

  {/* 세부내용 */}
  <td
    style={{
      backgroundColor: "white",
      padding: "16px 20px",
      textAlign: "left",
      display: "flex",
      flexDirection: "column",
      gap: "8px",
    }}
  >
    <label>
      <input
        type="radio"
        name="reasonType"
        value="basic"
        checked={state.reasonType === "basic"}
        onChange={handleChange}
      />{" "}
      거래 사실 없음
    </label>
    <label>
      <input
        type="radio"
        name="reasonType"
        value="missing"
        checked={state.reasonType === "missing"}
        onChange={handleChange}
      />{" "}
      거래 있음 + 자료 미제출
      <br />
      항목 수:{" "}
      <input
        type="number"
        name="missingItems"
        min="0"
        value={state.missingItems}
        onChange={handleChange}
        style={{
          width: 70,
          marginLeft: 10,
          borderRadius: 7,
          border: "1.8px solid #ced4da",
          padding: 8,
        }}
      />
    </label>
  </td>

  {/* 거래처 수(수량) */}
  <td
    style={{
      backgroundColor: "white",
      padding: "16px 20px",
      textAlign: "center",
    }}
  >
    <input
      type="number"
      name="reasonPartners"
      min="0"
      value={state.reasonPartners || 0}
      onChange={handleChange}
      style={{
        width: 70,
        borderRadius: 7,
        border: "1.8px solid #ced4da",
        padding: 8,
      }}
    />
  </td>

  {/* 단가 */}
  <td
    style={{
      backgroundColor: "white",
      padding: "16px 20px",
      textAlign: "center",
    }}
  >
    기본 100,000 + 항목당 30,000
  </td>

  {/* 합계 */}
  <td
    style={{
      backgroundColor: "white",
      padding: "16px 20px",
      textAlign: "right",
    }}
  >
{(() => {
  const partners = state.reasonPartners || 0;
  const missingCount = state.missingItems || 0;
  if (!state.includeReason) return 0;
  if (state.reasonType === "basic") {
    return (100000 * partners).toLocaleString();
  } else if (state.reasonType === "missing") {
    const sum = 100000 * partners + 30000 * missingCount;
    return sum.toLocaleString();
  }
  return 0;
})()}

  </td>
</tr>






    {/* 과세자료 제출 서류 정비 및 전자제출 대행 */}
    <tr>
      <td
        style={{
          backgroundColor: "white",
          padding: "16px 20px",
          textAlign: "center",
        }}
      >
        <input
          type="checkbox"
          name="includeDoc"
          checked={state.includeDoc}
          onChange={handleChange}
        />
      </td>
      <td style={{ backgroundColor: "white", padding: "16px 20px" }}>
        <b>과세자료 제출 서류 정비 및 전자제출 대행</b>
      </td>
      <td
        style={{
          backgroundColor: "white",
          padding: "16px 20px",
          textAlign: "left",
        }}
      >
        거래처 수: -
      </td>
      <td
        style={{
          backgroundColor: "white",
          padding: "16px 20px",
          textAlign: "center",
        }}
      >
        <input
          type="number"
          name="docPartners"
          min="0"
          value={state.docPartners}
          onChange={handleChange}
          style={{
            width: 70,
            borderRadius: 7,
            border: "1.8px solid #ced4da",
            padding: 8,
          }}
        />
      </td>
      <td
        style={{
          backgroundColor: "white",
          padding: "16px 20px",
          textAlign: "center",
        }}
      >
        50,000원
      </td>
      <td
        style={{
          backgroundColor: "white",
          padding: "16px 20px",
          textAlign: "right",
        }}
      >
        {costs.docCost.toLocaleString()}
      </td>
    </tr>

    {/* 과세가격 결정 사유 보고서 작성 */}
    <tr>
      <td
        style={{
          backgroundColor: "white",
          padding: "16px 20px",
          textAlign: "center",
        }}
      >
        <input
          type="checkbox"
          name="includeReport"
          checked={state.includeReport}
          onChange={handleChange}
        />
      </td>
      <td style={{ backgroundColor: "white", padding: "16px 20px" }}>
        <b>
          과세가격 결정 사유 보고서 작성
          <br />
          <small>(로열티, 생산지원비 등 해당 항목별)</small>
        </b>
      </td>
      <td style={{ backgroundColor: "white", padding: "16px 20px" }}>
        {/* 세부내용은 선택된 항목 이름만 , 입력은 거래처 수/수량 칸에서 */}
        {Object.entries(state.reportItems)
          .filter(([key, checked]) => checked)
          .map(([key]) => {
            if (key === "royalty") return "로열티";
            if (key === "productionSupport") return "생산지원비";
            if (key === "commission") return "수수료/중개료";
            if (key === "freightInsurance") return "운임/보험료";
            if (key === "packaging") return "용기/포장비";
            if (key === "postBenefit") return "사후귀속이익";
            if (key === "indirectPayment") return "간접지급금액";
            return "";
          })
          .join(", ")}
      </td>
      <td
        style={{
          backgroundColor: "white",
          padding: "16px 20px",
          textAlign: "center",
          verticalAlign: "top",
        }}
      >
        {/* 거래처 수(수량) 입력란: 체크된 항목 수 */}
        {Object.values(state.reportItems).filter(Boolean).length > 0 ? (
          <input
            type="number"
            name="reportCount"
            min="0"
            value={Object.values(state.reportItems).filter(Boolean).length}
            readOnly
            style={{
              width: 70,
              borderRadius: 7,
              border: "1.8px solid #ced4da",
              padding: 8,
              backgroundColor: "#eee",
              cursor: "not-allowed",
            }}
          />
        ) : (
          "-"
        )}
      </td>
      <td
        style={{
          backgroundColor: "white",
          padding: "16px 20px",
          textAlign: "center",
        }}
      >
        1,000,000원/항목
      </td>
      <td
        style={{
          backgroundColor: "white",
          padding: "16px 20px",
          textAlign: "right",
        }}
      >
        {costs.reportCost.toLocaleString()}
      </td>
    </tr>

    {/* 특수관계자 거래 분석 및 이전가격 검토 리포트 */}
    <tr>
      <td
        style={{
          backgroundColor: "white",
          padding: "16px 20px",
          textAlign: "center",
        }}
      >
        <input
          type="checkbox"
          name="includeRelated"
          checked={state.includeRelated}
          onChange={handleChange}
        />
      </td>
      <td style={{ backgroundColor: "white", padding: "16px 20px" }}>
        <b>특수관계자 거래 분석 및 이전가격 검토 리포트</b>
      </td>
      <td
        style={{
          backgroundColor: "white",
          padding: "16px 20px",
          textAlign: "left",
        }}
      >
        거래처 수: -
      </td>
      <td
        style={{
          backgroundColor: "white",
          padding: "16px 20px",
          textAlign: "center",
        }}
      >
        {/* 수입금액 드롭다운 */}
        <select
          name="relatedImportAmount"
          value={state.relatedImportAmount}
          onChange={handleChange}
          style={{
            width: 130,
            borderRadius: 8,
            border: "1.8px solid #ced4da",
            padding: 8,
          }}
        >
          <option value={1}>50억 미만</option>
          <option value={2}>50~100억</option>
          <option value={3}>100~200억</option>
          <option value={4}>200~500억</option>
          <option value={5}>500억 이상</option>
        </select>
      </td>
      <td
        style={{
          backgroundColor: "white",
          padding: "16px 20px",
          textAlign: "center",
        }}
      >
        -
      </td>
      <td
        style={{
          backgroundColor: "white",
          padding: "16px 20px",
          textAlign: "right",
        }}
      >
        {costs.relatedCost.toLocaleString()}
      </td>
    </tr>
  </tbody>

        <tfoot>
          <tr>
            <th
              colSpan="4"
              style={{
                padding: "16px 20px",
                textAlign: "right",
                borderRadius: "0 0 0 10px",
              }}
            >
              V.A.T (10%)
            </th>
            <th
              style={{
                padding: "16px 20px",
                textAlign: "right",
              }}
            >
              {costs.vat.toLocaleString()}
            </th>
          </tr>
          <tr>
            <th
              colSpan="4"
              style={{
                padding: "16px 20px",
                textAlign: "right",
              }}
            >
              총 합계 (V.A.T 포함)
            </th>
            <th
              style={{
                padding: "16px 20px",
                textAlign: "right",
                borderRadius: "0 0 10px 0",
              }}
            >
              {costs.totalCost.toLocaleString()}
            </th>
          </tr>
          <tr>
            <td colSpan="5" style={{ padding: "16px 20px" }}>
              <label htmlFor="note" style={{ fontWeight: 600 }}>
                특이사항 / 비고:
              </label>
              <br />
              <textarea
                id="note"
                name="note"
                value={state.note}
                onChange={handleNoteChange}
                rows={4}
                style={{
                  width: "100%",
                  marginTop: 6,
                  borderRadius: 8,
                  border: "1.8px solid #ced4da",
                  padding: 12,
                  fontSize: 15,
                  resize: "vertical",
                }}
                placeholder="특이사항이 있으면 자유롭게 기재해 주세요."
              />
            </td>
          </tr>
        </tfoot>
      </table>

      {/* 숨겨진 input들 */}
      {renderHiddenInputs()}

      <button type="button" onClick={calculateCosts} className="submit-btn">
        💰 견적 계산하기
      </button>
      <button type="submit" className="submit-btn" style={{ marginTop: 10 }}>
        견적서 생성
      </button>
    </form>
  );
}
