import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function QuotationConsulting() {
  const navigate = useNavigate();

  const [form, setForm] = useState({
    company: "",
    doc_number: "",
    items: [
      { id: 1, label: "Í¥ÄÏÑ∏ ÌôòÍ∏â Ïª®ÏÑ§ÌåÖ", checked: false, amount: "", note: "" },
      { id: 2, label: "Í≥ºÏÑ∏ÏûêÎ£å ÎåÄÏùë ÏûêÎ¨∏", checked: false, amount: "", note: "" },
      { id: 3, label: "Í≥ºÏÑ∏Í∞ÄÍ≤© ÏÇ¨Ï†ÑÏã¨ÏÇ¨ ÎåÄÏùë", checked: false, amount: "", note: "" },
      { id: 4, label: "Ïù¥Ï†ÑÍ∞ÄÍ≤© Í¥ÄÎ†® Î∂ÑÏÑù", checked: false, amount: "", note: "" },
    ],
  });

  const handleChange = (e, id, field) => {
    const updatedItems = form.items.map((item) =>
      item.id === id ? { ...item, [field]: field === "checked" ? e.target.checked : e.target.value } : item
    );
    setForm({ ...form, items: updatedItems });
  };

  const handleBasicChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const selectedData = form.items
      .filter((item) => item.checked)
      .map((item) => ({
        label: item.label,
        value: item.amount,
        note: item.note,
      }));

    navigate("/preview", {
      state: {
        selectedData,
        company: form.company,
        doc_number: form.doc_number,
      },
    });
  };

  return (
    <form
      onSubmit={handleSubmit}
      style={{
        maxWidth: 900,
        margin: "0 auto",
        padding: 30,
        fontFamily: "'Pretendard', sans-serif",
        backgroundColor: "#f9f9f9",
        borderRadius: 8,
      }}
    >
      {/* Ìó§Îçî */}
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: 30 }}>
        <div style={{ width: 180 }}>
          <img
            src={`${process.env.PUBLIC_URL}/images/HYLOGO_NAVY.png`}
            alt="ÌïúÏòÅ Î°úÍ≥†"
            style={{ width: "100%", height: "auto", objectFit: "contain" }}
          />
        </div>
        <h2 style={{ fontSize: 26, fontWeight: 700, color: "#1a2e59" }}>Ïª®ÏÑ§ÌåÖ Í≤¨Ï†ÅÏÑú</h2>
      </div>

      {/* ÏóÖÏ≤¥Î™Ö, Î¨∏ÏÑúÎ≤àÌò∏ */}
      <div style={{ display: "flex", gap: 20, marginBottom: 40 }}>
        <div style={{ flex: 1 }}>
          <label style={{ display: "block", fontWeight: 600, marginBottom: 6 }}>ÏóÖÏ≤¥Î™Ö</label>
          <input
            type="text"
            name="company"
            value={form.company}
            onChange={handleBasicChange}
            placeholder="ÏóÖÏ≤¥Î™ÖÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
            style={{ width: "80%", padding: 10, borderRadius: 6, border: "1.5px solid #ccc" }}
            required
          />
        </div>
        <div style={{ flex: 1 }}>
          <label style={{ display: "block", fontWeight: 600, marginBottom: 6 }}>Î¨∏ÏÑúÎ≤àÌò∏</label>
          <input
            type="text"
            name="doc_number"
            value={form.doc_number}
            onChange={handleBasicChange}
            placeholder="Î¨∏ÏÑúÎ≤àÌò∏ ÏûÖÎ†•"
            style={{ width: "100%", padding: 10, borderRadius: 6, border: "1.5px solid #ccc" }}
            required
          />
        </div>
      </div>

      {/* Ìëú */}
      <table style={{ width: "100%", borderCollapse: "collapse", marginBottom: 30 }}>
        <thead>
          <tr style={{ backgroundColor: "#3f72af", color: "#fff" }}>
            <th style={{ padding: 12 }}>‚úî</th>
            <th style={{ padding: 12, width: "30%" }}>Ìï≠Î™©</th>
            <th style={{ padding: 12, width: "25%" }}>Í∏àÏï°</th>
            <th style={{ padding: 12, width: "45%" }}>ÎπÑÍ≥†</th>
          </tr>
        </thead>
        <tbody>
          {form.items.map((item) => (
            <tr key={item.id} style={{ backgroundColor: "#fff", borderBottom: "1px solid #ddd" }}>
              <td style={{ textAlign: "center" }}>
                <input
                  type="checkbox"
                  checked={item.checked}
                  onChange={(e) => handleChange(e, item.id, "checked")}
                />
              </td>
              <td style={{ padding: 10 }}>{item.label}</td>
              <td style={{ padding: 10 }}>
                <input
                  type="text"
                  value={item.amount}
                  onChange={(e) => handleChange(e, item.id, "amount")}
                  placeholder="Ïòà: 100000"
                  style={{ width: "100px", padding: 6, textAlign: "right" }}
                />
                &nbsp;Ïõê
              </td>
              <td style={{ padding: 10 }}>
                <input
                  type="text"
                  value={item.note}
                  onChange={(e) => handleChange(e, item.id, "note")}
                  placeholder="ÎπÑÍ≥† ÏûÖÎ†•"
                  style={{ width: "100%", padding: 6 }}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Ï†úÏ∂ú Î≤ÑÌäº */}
      <div style={{ textAlign: "center", marginTop: 30 }}>
        <button
          type="submit"
          style={{
            backgroundColor: "#3f72af",
            color: "#fff",
            border: "none",
            borderRadius: 6,
            padding: "12px 30px",
            fontSize: 16,
            fontWeight: 600,
            cursor: "pointer",
          }}
        >
          üìÑ Í≤¨Ï†ÅÏÑú ÏÉùÏÑ±
        </button>
      </div>
    </form>
  );
}
