// QuoteForm.js (Î≥ÄÍ≤ΩÎ∂Ñ Ìè¨Ìï® Ï†ÑÏ≤¥)
import { useNavigate } from "react-router-dom";
import React, { useState, useEffect, useCallback } from "react";
import { HY_BRANCHES } from "./hyBranches"; // ‚¨ÖÔ∏è Ï∂îÍ∞Ä

export default function QuoteForm() {
  const navigate = useNavigate();

  const [state, setState] = useState({
    // üîπ ÏßÄÏÇ¨ ÏÑ†ÌÉù (Í∏∞Î≥∏: Ïù∏Ï≤ú)
    branchKey: "IC",

    company: "",
    doc_number: "",

    includeBase: false,
    partners: 0,
    importAmount: 1, // 1~4

    includeReason: false,
    reasonPartners: 0,

    includeDelayReason: false,
    delayReasonPartners: 0,

    includeDoc: false,
    docPartners: 0,

    includeReport: false,
    reportItems: {
      royalty: false,
      productionSupport: false,
      commission: false,
      freightInsurance: false,
      packaging: false,
      postBenefit: false,
      indirectPayment: false,
      other: false,
    },
    reportCounts: {
      royalty: 0, productionSupport: 0, commission: 0, freightInsurance: 0,
      packaging: 0, postBenefit: 0, indirectPayment: 0, other: 0,
    },
    reportPartners: 0,

    includeRelated: false,
    relatedImportAmount: 1, // 1~4

    note: "",
    discountRate: 0,
  });

  const [costs, setCosts] = useState({
    baseCost: 0,
    reasonCost: 0,
    delayReasonCost: 0,
    docCost: 0,
    reportCost: 0,
    relatedCost: 0,
    subTotal: 0,
    discountAmount: 0,
    vat: 0,
    totalCost: 0,
  });

  const handleChange = (e) => {
    const { name, type, checked, value } = e.target;

    if (type === "checkbox" && name.startsWith("reportItems")) {
      const key = name.split(".")[1];
      setState((prev) => ({
        ...prev,
        reportItems: { ...prev.reportItems, [key]: checked },
      }));
    } else if (type === "checkbox") {
      setState((prev) => ({ ...prev, [name]: checked }));
    } else {
      setState((prev) => ({
        ...prev,
        [name]:
          type === "number" ||
          name === "importAmount" ||
          name === "relatedImportAmount"
            ? Number(value)
            : value,
      }));
    }
  };

  const handleNoteChange = (e) => {
    setState((prev) => ({ ...prev, note: e.target.value }));
  };

  // (1) Í∏∞Î≥∏ Ìï≠Î™©: Í±∞ÎûòÏ≤ò Í∏∞Ï§Ä + Í∞ÄÏ§ëÏπò
  const calcBaseCost = (partners, importAmount) => {
    const p = Number(partners) || 0;
    const ia = Number(importAmount) || 1;

    const base =
      p <= 10 ? 1000000 :
      p <= 20 ? 2000000 :
      p <= 50 ? 3000000 :
      5000000;

    const weight = ia === 1 ? 0 : ia === 2 ? 0.2 : ia === 3 ? 0.3 : 0.5;
    return Math.round(base * (1 + weight));
