  const handleSubmit = (e) => {
    e.preventDefault();

    // ÎπÑÏö© Ïû¨Í≥ÑÏÇ∞Í≥º ÎèôÏùº Î°úÏßÅ (ÏÉùÎûµ Í∞ÄÎä•ÌïòÏßÄÎßå ÏïàÏ†ÑÌïòÍ≤å Ïú†ÏßÄ)
    let subTotal = 0;

