import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import HY_BRANCHES from "./hyBranches"; // ‚Üê ÏßÄÏÇ¨ Î™©Î°ù Í∞ÄÏ†∏Ïò§Í∏∞


export default function QuotationConsulting() {
  const navigate = useNavigate();


  
  const [form, setForm] = useState({
    company: "",
    doc_number: "",
    discountRate: 0,
      type: "quotation", // Í∏∞Î≥∏Í∞í

    items: [{ id: 1, label: "", qty: "", unit: "", amount: 0, note: "" }],
  });

  const handleChange = (e, id, field) => {
    const value = e.target.value;
    const updatedItems = form.items.map((item) => {
      if (item.id === id) {
        const newItem = { ...item, [field]: value };
        const qty = parseFloat(newItem.qty) || 0;
        const unit = parseFloat(newItem.unit) || 0;
        newItem.amount = qty * unit;
        return newItem;
      }
      return item;
    });
    setForm({ ...form, items: updatedItems });
  };

  const handleBasicChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: name === "discountRate" ? Number(value) : value });
  };

  const addRow = () => {
    const newId = form.items.length ? form.items[form.items.length - 1].id + 1 : 1;
    setForm({
      ...form,
      items: [
        ...form.items,
        { id: newId, label: "", qty: "", unit: "", amount: 0, note: "" },
      ],
    });
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    const selectedData = form.items
      .filter((item) => item.label || item.amount)
      .map((item) => ({
        label: item.label,
        qty: item.qty,
        unit: item.unit,
        value: item.amount,
        note: item.note,
      }));

    const subtotal = form.items.reduce((sum, item) => sum + (Number(item.amount) || 0), 0);
    const discountAmount = Math.round(subtotal * (form.discountRate / 100));
    const discounted = subtotal - discountAmount;
    const vat = Math.round(discounted * 0.1);
    const totalCost = discounted + vat;

    navigate("/consultingpreview", {
      state: {
        selectedData,
        company: form.company,
        doc_number: form.doc_number,
        discountRate: form.discountRate,
        discountAmount,
        vat,
        totalCost,
        note: form.note || "",
            type: form.type, // <-- Ï∂îÍ∞Ä
            branchKey: form.branchKey,

      },
    });
  };


  const subtotal = form.items.reduce((sum, item) => sum + (Number(item.amount) || 0), 0);
  const discount = Math.round(subtotal * (form.discountRate / 100));
  const discounted = subtotal - discount;
  const vat = Math.round(discounted * 0.1);
  const total = discounted + vat;

  return (
    <form
      onSubmit={handleSubmit}
      style={{
        maxWidth: 900,
        margin: "0 auto",
        padding: 30,
        fontFamily: "'Pretendard', sans-serif",
        backgroundColor: "#f9f9f9",
        borderRadius: 8,
      }}
    >
      {/* Ìó§Îçî */}
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: 30 }}>
        <div style={{ width: 180 }}>
          <img
            src={`${process.env.PUBLIC_URL}/images/HYLOGO_NAVY.png`}
            alt="ÌïúÏòÅ Î°úÍ≥†"
            style={{ width: "100%", height: "auto", objectFit: "contain" }}
          />
        </div>
        <h2 style={{ fontSize: 26, fontWeight: 700, color: "#1a2e59" }}>Ïª®ÏÑ§ÌåÖ Í≤¨Ï†ÅÏÑú</h2>
      </div>

{/* Î¨∏ÏÑú ÌÉÄÏûÖ ÏÑ†ÌÉù (Invoice / Quotation) */}
<div style={{ marginBottom: 20 }}>
  <label style={{ fontWeight: 600, display: "block", marginBottom: 8 }}>
    Î¨∏ÏÑú Ïú†Ìòï ÏÑ†ÌÉù:
  </label>
  <label style={{ marginRight: 20 }}>
    <input
      type="radio"
      name="type"
      value="invoice"
      checked={form.type === "invoice"}
      onChange={handleBasicChange}
      style={{ marginRight: 6 }}
    />
    Invoice
  </label>
  <label>
    <input
      type="radio"
      name="type"
      value="quotation"
      checked={form.type === "quotation"}
      onChange={handleBasicChange}
      style={{ marginRight: 6 }}
    />
    Quotation
  </label>
</div>

      {/* ÏßÄÏÇ¨ ÏÑ†ÌÉù */}
      <div style={{ marginBottom: 20 }}>
        <label style={{ fontWeight: 600, display: "block", marginBottom: 8 }}>ÏßÄÏÇ¨ ÏÑ†ÌÉù:</label>
        {Object.values(HY_BRANCHES).map((branch) => (
          <label key={branch.key} style={{ marginRight: 20 }}>
            <input
              type="radio"
              name="branchKey"
              value={branch.key}
              checked={form.branchKey === branch.key}
              onChange={handleBasicChange}
              style={{ marginRight: 6 }}
            />
            {branch.label}
          </label>
        ))}
      </div>


      {/* ÏóÖÏ≤¥Î™Ö, Î¨∏ÏÑúÎ≤àÌò∏ */}
      <div style={{ display: "flex", gap: 20, marginBottom: 40 }}>
        <div style={{ width: "50%" }}>
          <label style={{ display: "block", fontWeight: 600, marginBottom: 6 }}>ÏóÖÏ≤¥Î™Ö</label>
          <input
            type="text"
            name="company"
            value={form.company}
            onChange={handleBasicChange}
            placeholder="ÏóÖÏ≤¥Î™ÖÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
            style={{ width: "90%", padding: 10, borderRadius: 6, border: "1.5px solid #ccc" }}
            required
          />
        </div>
        <div style={{ width: "50%" }}>
          <label style={{ display: "block", fontWeight: 600, marginBottom: 6 }}>Î¨∏ÏÑúÎ≤àÌò∏</label>
          <input
            type="text"
            name="doc_number"
            value={form.doc_number}
            onChange={handleBasicChange}
            placeholder="Î¨∏ÏÑúÎ≤àÌò∏ ÏûÖÎ†•"
            style={{ width: "90%", padding: 10, borderRadius: 6, border: "1.5px solid #ccc" }}
            required
          />
        </div>
      </div>

      {/* ÌÖåÏù¥Î∏î */}
      <table style={{ width: "100%", borderCollapse: "collapse", marginBottom: 20 }}>
        <thead>
          <tr style={{ backgroundColor: "#3f72af", color: "#fff" }}>
            <th style={{ padding: 12, width: "40%" }}>Ìï≠Î™©</th>
            <th style={{ padding: 12, width: "10%" }}>ÏàòÎüâ</th>
            <th style={{ padding: 12, width: "15%" }}>Îã®Í∞Ä</th>
            <th style={{ padding: 12, width: "10%" }}>Í∏àÏï°</th>
            <th style={{ padding: 12, width: "25%" }}>ÎπÑÍ≥†</th>
          </tr>
        </thead>
        <tbody>
          {form.items.map((item) => (
            <tr key={item.id} style={{ borderBottom: "1px solid #ddd" }}>
              <td style={{ padding: 10 }}>
                <input
                  type="text"
                  value={item.label}
                  onChange={(e) => handleChange(e, item.id, "label")}
                  placeholder="Ìï≠Î™© ÏûÖÎ†•"
                  style={{ width: "100%", padding: 6 }}
                />
              </td>
              <td style={{ padding: 10 }}>
                <input
                  type="number"
                  value={item.qty}
                  onChange={(e) => handleChange(e, item.id, "qty")}
                  placeholder="ÏàòÎüâ"
                  style={{ width: "100%", padding: 6, textAlign: "right" }}
                />
              </td>
              <td style={{ padding: 10 }}>
                <input
                  type="number"
                  value={item.unit}
                  onChange={(e) => handleChange(e, item.id, "unit")}
                  placeholder="Îã®Í∞Ä"
                  style={{ width: "60%", padding: 6, textAlign: "right" }}
                />
              </td>
              <td style={{ padding: 10, textAlign: "right" }}>
                {item.amount.toLocaleString()} Ïõê
              </td>
              <td style={{ padding: 10 }}>
                <input
                  type="text"
                  value={item.note}
                  onChange={(e) => handleChange(e, item.id, "note")}
                  placeholder="ÎπÑÍ≥† ÏûÖÎ†•"
                  style={{ width: "100%", padding: 6 }}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* ‚ûï Î≤ÑÌäº */}
      <div style={{ textAlign: "left", marginBottom: 30 }}>
        <button
          type="button"
          onClick={addRow}
          style={{
            backgroundColor: "#1a2e59",
            color: "#fff",
            padding: "6px 14px",
            borderRadius: 6,
            border: "none",
            fontWeight: 600,
            cursor: "pointer",
          }}
        >
          ‚ûï Ìï≠Î™© Ï∂îÍ∞Ä
        </button>
      </div>

      {/* Ìï†Ïù∏Ïú® ÏûÖÎ†• Î∞è Ìï©Í≥Ñ */}
      <div
  style={{
    textAlign: "right",
    fontSize: 15,
    marginBottom: 30,
    lineHeight: 2,           // ÌñâÍ∞Ñ Ï°∞Ï†ï
    display: "flex",
    flexDirection: "column",
    gap: 8,                  // Í∞Å Ï§Ñ Í∞ÑÍ≤© Ï∂îÍ∞Ä
  }}
>
  <div>Ï¥ù Ìï©Í≥Ñ: {subtotal.toLocaleString()} Ïõê</div>

  <div>
    Ìï†Ïù∏Ïú®:&nbsp;
    <input
      type="number"
      name="discountRate"
      min="0"
      max="100"
      value={form.discountRate}
      onChange={handleBasicChange}
      style={{
        width: 60,
        padding: 6,
        borderRadius: 6,
        border: "1px solid #ccc",
        marginRight: 6,
      }}
    />
    % (-{discount.toLocaleString()} Ïõê)
  </div>

  <div>VAT (10%): {vat.toLocaleString()} Ïõê</div>

  <div style={{ fontWeight: 700, fontSize: 16, marginTop: 10 }}>
    ÏµúÏ¢Ö Ìï©Í≥Ñ: {total.toLocaleString()} Ïõê
  </div>
</div>

{/* ÌäπÏù¥ÏÇ¨Ìï≠ / ÎπÑÍ≥† ÏûÖÎ†•ÎûÄ */}
<div style={{ marginBottom: 30 }}>
  <label htmlFor="note" style={{ fontWeight: 600, display: "block", marginBottom: 6 }}>
    ÌäπÏù¥ÏÇ¨Ìï≠ / ÎπÑÍ≥†:
  </label>
  <textarea
    id="note"
    name="note"
    value={form.note}
    onChange={handleBasicChange}
    rows={4}
    style={{
      width: "100%",
      borderRadius: 8,
      border: "1.8px solid #ced4da",
      padding: 12,
      fontSize: 15,
      resize: "vertical",
    }}
    placeholder="ÌäπÏù¥ÏÇ¨Ìï≠Ïù¥ ÏûàÏúºÎ©¥ ÏûêÏú†Î°≠Í≤å Í∏∞Ïû¨Ìï¥ Ï£ºÏÑ∏Ïöî."
  />
</div>


<div
  style={{
    display: "flex",
    justifyContent: "center",
    gap: 20, // Î≤ÑÌäº ÏÇ¨Ïù¥ Í∞ÑÍ≤©
    marginTop: 30,
  }}
>
  <button
    onClick={() => navigate("/")}
    style={{
      backgroundColor: "#aaa",
      border: "none",
      padding: "12px 30px", // ÎÜíÏù¥ ÎßûÏ∂îÍ∏∞ ÏúÑÌï¥ ÏàòÏ†ï
      borderRadius: 6,
      cursor: "pointer",
      fontWeight: 600,
      fontSize: 16, // Í∏ÄÏûê ÌÅ¨Í∏∞ ÎßûÏ∂îÍ∏∞
      color: "#fff",
    }}
  >
    Îã§ÏãúÌïòÍ∏∞
  </button>

      {/* Ï†úÏ∂ú */}
      <div style={{ textAlign: "center" }}>
        <button
          type="submit"
          style={{
            backgroundColor: "#3f72af",
            color: "#fff",
            border: "none",
            borderRadius: 6,
            padding: "12px 30px",
            fontSize: 16,
            fontWeight: 600,
            cursor: "pointer",
          }}
        >
          üìÑ Í≤¨Ï†ÅÏÑú ÏÉùÏÑ±
        </button>
      </div>
      </div>
    </form>
  );
}
