import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import jsPDF from "jspdf";
import html2canvas from "html2canvas";

export default function CustomsPreview() {
  const location = useLocation();
  const navigate = useNavigate();

  const { selectedData, company, doc_number } = location.state || {};
  const today = new Date();

  const dateString = `${String(today.getFullYear()).slice(2)}${String(today.getMonth() + 1).padStart(2, "0")}${String(today.getDate()).padStart(2, "0")}`;

  const downloadPDF = () => {
    const doc = new jsPDF("p", "pt", "a4");
    const input = document.getElementById("invoice-content");

    const companyNameSafe = company?.replace(/[^Í∞Ä-Ìû£a-zA-Z0-9\s]/g, "") || "ÏóÖÏ≤¥Î™ÖÏóÜÏùå";
    const filename = `[Í¥ÄÏÑ∏Î≤ïÏù∏ÌïúÏòÅ] ${companyNameSafe} ÌÜµÍ¥Ä ÏàòÏàòÎ£å Í≤¨Ï†ÅÏÑú_${dateString}.pdf`;

    html2canvas(input, { scale: 2 }).then((canvas) => {
      const imgData = canvas.toDataURL("image/png");
      const pdfWidth = doc.internal.pageSize.getWidth() - 40;
      const imgProps = doc.getImageProperties(imgData);
      const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;

      doc.addImage(imgData, "PNG", 20, 20, pdfWidth, pdfHeight);
      doc.save(filename);
    });
  };

    const labelMap = {
    feeRate: "ÏàòÏàòÎ£å ÏöîÏú®",
    minFee: "MIN",
    maxFee: "MAX",
    correctionFee: "Ïã†Í≥†Ï†ïÏ†ï",
    ftaApplication: "FTA ÌòëÏ†ïÏã†Ï≤≠",
    requirementApplication: "ÏöîÍ±¥Ïã†Ï≤≠",
    exportFeeRate: "ÏàòÏàòÎ£å ÏöîÏú®",
    exportMinFee: "MIN",
    exportMaxFee: "MAX",
    exportCorrectionFee: "Ïã†Í≥†Ï†ïÏ†ï",
    exportFtaCertificateAuthorized: "FTA C/O Î∞úÍ∏â (Ïù∏Ï¶ùÏàòÏ∂úÏûê)",
    exportFtaCertificateUnauth: "FTA C/O Î∞úÍ∏â (ÎπÑÏù∏Ï¶ùÏàòÏ∂úÏûê)",
  };

  
  const importItems = selectedData.filter((item) => !item.label.startsWith("export"));
  const exportItems = selectedData.filter((item) => item.label.startsWith("export"));

  const renderTable = (title, items) => (
    <>
      <h3 style={{ fontSize: 18, margin: "40px 0 10px", color: "#1a2e59" }}>{title}</h3>
      <table style={{ width: "100%", borderCollapse: "collapse", fontSize: 14 }}>
        <thead>
          <tr style={{ backgroundColor: "#3f72af", color: "#fff" }}>
            <th style={{ width: "30%", padding: 12, textAlign: "left" }}>Ìï≠Î™©</th>
            <th style={{ width: "30%", padding: 12, textAlign: "left" }}>Í∏àÏï°</th>
            <th style={{ width: "40%", padding: 12, textAlign: "left" }}>ÎπÑÍ≥†</th>
          </tr>
        </thead>
        <tbody>
          {items.map(({ label, value, note }, i) => (
            <tr key={i} style={{ backgroundColor: "#fff", borderBottom: "1px solid #ddd" }}>
              <td style={{ padding: "10px 12px" }}>{labelMap[label] || label}</td>
              <td style={{ padding: "10px 12px" }}>
                {value ? (label.includes("Rate") ? `${value}%` : `${Number(value).toLocaleString()} Ïõê`) : "-"}
              </td>
              <td style={{ padding: "10px 12px" }}>{note || "-"}</td>
            </tr>
          ))}
          <tr>
            <td colSpan={3} style={{ padding: "12px 10px", fontWeight: 600, textAlign: "right", color: "#555" }}>
              ‚Äª V.A.T Î≥ÑÎèÑ
            </td>
          </tr>
        </tbody>
      </table>
