import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import jsPDF from "jspdf";
import html2canvas from "html2canvas";

export default function Preview() {
  const location = useLocation();
  const navigate = useNavigate();

  const formData = location.state?.formData || {};
  const costs = location.state?.costs || {};

  const today = new Date();
  const dateString = `${String(today.getFullYear()).slice(2)}${String(
    today.getMonth() + 1
  ).padStart(2, "0")}${String(today.getDate()).padStart(2, "0")}`;

  const downloadPDF = () => {
    const doc = new jsPDF("p", "pt", "a4");
    const input = document.getElementById("invoice-content");

    const companyNameSafe = formData.company
      ? formData.company.replace(/[^Í∞Ä-Ìû£a-zA-Z0-9\s]/g, "")
      : "ÏóÖÏ≤¥Î™ÖÏóÜÏùå";
    const filename = `[Í¥ÄÏÑ∏Î≤ïÏù∏ÌïúÏòÅ] ${companyNameSafe} Í≥ºÏÑ∏ÏûêÎ£å Ïª®ÏÑ§ÌåÖ Í≤¨Ï†ÅÏÑú_${dateString}.pdf`;

    html2canvas(input, { scale: 2 }).then((canvas) => {
      const imgData = canvas.toDataURL("image/png");
      const pdfWidth = doc.internal.pageSize.getWidth() - 40;
      const imgProps = doc.getImageProperties(imgData);
      const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;

      doc.addImage(imgData, "PNG", 20, 20, pdfWidth, pdfHeight);
      doc.save(filename);
    });
  };

  const importAmountMap = {
    1: "10Ïñµ ÎØ∏Îßå",
    2: "10Ïñµ Ïù¥ÏÉÅ ~ 50Ïñµ ÎØ∏Îßå",
    3: "50Ïñµ Ïù¥ÏÉÅ",
  };

  const reportItemLabels = {
    royalty: "Î°úÏó¥Ìã∞",
    productionSupport: "ÏÉùÏÇ∞ÏßÄÏõêÎπÑ",
    freightInsurance: "Ïö¥ÏûÑ/Î≥¥ÌóòÎ£å",
    commission: "Ïª§ÎØ∏ÏÖò",
    tools: "Í∏àÌòïÎπÑ/Î¨¥ÏÉÅÏ†úÍ≥µ",
    others: "Í∏∞ÌÉÄ",
  };

  const serviceItems = [
    {
      label: "Í±∞ÎûòÍµ¨Ï°∞ Î∞è Í≥ºÏÑ∏ÏûêÎ£å Ï†úÏ∂ú Î≤îÏúÑ Í≤ÄÌÜ†",
      checked: formData.includeBase,
      quantity: formData.partners || 1,
      total: costs.baseCost,
      details: `Ïó∞ ÏàòÏûÖÍ∏àÏï°: ${importAmountMap[formData.importAmount] || "ÎØ∏ÏûÖÎ†•"}`,
    },
    {
      label: "Í≥ºÏÑ∏ÏûêÎ£å ÎØ∏Ï†úÏ∂ú ÏÇ¨Ïú†ÏÑú ÏûëÏÑ± ÏßÄÏõê",
      checked: formData.includeReason,
      quantity: formData.reasonPartners || 1,
      total: costs.reasonCost,
      details:
        formData.reasonType === "basic"
          ? "Í±∞Îûò ÏÇ¨Ïã§ ÏóÜÏùå"
          : `ÎπÑÍ≥ºÏÑ∏ Í±∞Îûò (Ìï≠Î™© Ïàò: ${formData.missingItems || 0})`,
    },
    {
      label: "Í≥ºÏÑ∏ÏûêÎ£å Ï†úÏ∂ú ÏÑúÎ•ò Ï†ïÎπÑ Î∞è Ï†ÑÏûêÏ†úÏ∂ú ÎåÄÌñâ",
      checked: formData.includeDoc,
      quantity: formData.docPartners || 1,
      total: costs.docCost,
      details: ``,
    },
    {
      label: "Í≥ºÏÑ∏Í∞ÄÍ≤© Í≤∞Ï†ï ÏÇ¨Ïú† Î≥¥Í≥†ÏÑú ÏûëÏÑ±",
      checked: formData.includeReport,
      quantity:
        Object.values(formData.reportItems || {}).filter(Boolean).length *
        (formData.reportPartners || 1),
      total: costs.reportCost,
      details: `${
        Object.entries(formData.reportItems || {})
