import React, { useState, useEffect, useMemo } from "react";
import { useNavigate } from "react-router-dom";

export default function QuotationFTA() {
  const navigate = useNavigate();

  const ftaMatrix = useMemo(() => [
    [30000, 25000, 20000, 15000, 10000, 5000, 3000],
    [null, 30000, 25000, 20000, 15000, 10000, 5000],
    [null, 35000, 30000, 25000, 20000, 15000, 10000],
    [null, 40000, 35000, 30000, 25000, 20000, 15000],
    [null, null, 40000, 35000, 30000, 25000, 20000],
    [null, null, 45000, 40000, 35000, 30000, 25000],
  ], []);

  const originDecisionMatrix = useMemo(() => [
    [15000, 12000, 10000, 8000, 5000, 3000, 2000],
    [18000, 15000, 12000, 10000, 8000, 5000, 3000],
    [20000, 18000, 15000, 12000, 10000, 8000, 5000],
    [22000, 20000, 18000, 15000, 12000, 10000, 8000],
  ], []);

  const originDocMatrix = useMemo(() => [
    [150000, 200000, 250000, 300000, 350000, 400000, 450000],
    [300000, 350000, 400000, 450000, 500000, 550000, 600000],
    [500000, 600000, 700000, 800000, 900000, 1000000, 1100000],
    [1000000, 1200000, 1500000, 2000000, 2500000, 3000000, 4000000],
    [2000000, 2500000, 3000000, 4000000, 5000000, 7000000, 10000000],
  ], []);

  const groupOptions = [
    "10개 이하",
    "10개 초과 30개 이하",
    "30개 초과 60개 이하",
    "60개 초과 100개 이하",
    "100개 초과 200개 이하",
    "200개 초과"
  ];

  const itemOptions = [
    "10개 이하",
    "10개 초과 100개 이하",
    "100개 초과 500개 이하",
    "500개 초과 1000개 이하",
    "1000개 초과 3000개 이하",
    "3000개 초과 10000개 이하",
    "10000개 초과"
  ];

  const originGroupOptions = ["10개 이하", "30개 이하", "60개 이하", "60개 초과"];
  const originClientOptions = ["3개 이하", "3개 초과 10개 이하", "10개 초과 50개 이하", "50개 초과 100개 이하", "100개 초과"];

  const [form, setForm] = useState({
    company: "",
    doc_number: "",
    groupIndex: 0,
    itemIndex: 0,
    qty: 1,
    discountRate: 0,
    note: "",
    includeFTA: false,
    includeOriginDecision: false,
    includeOriginDoc: false,
    originGroupIndex: 0,
    originClientIndex: 0
  });

  const [costs, setCosts] = useState({
    unitPrice: 0,
    totalAmount: 0,
    discount: 0,
    vat: 0,
    total: 0,
  });

  useEffect(() => {
    if (!form.includeFTA) {
      setCosts({ unitPrice: 0, totalAmount: 0, discount: 0, vat: 0, total: 0 });
      return;
    }
    const unit = ftaMatrix[form.groupIndex][form.itemIndex] ?? 0;
    const raw = unit * form.qty;
    const discount = Math.round(raw * (form.discountRate / 100));
    const vat = Math.round((raw - discount) * 0.1);
    const total = raw - discount + vat;
    setCosts({ unitPrice: unit, totalAmount: raw, discount, vat, total });
  }, [form.groupIndex, form.itemIndex, form.qty, form.discountRate, form.includeFTA, ftaMatrix]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : ["qty", "discountRate"].includes(name) ? Number(value) : value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    navigate("/preview", {
      state: {
        formData: form,
        costs,
        selectedData: []
      },
    });
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>원산지 판정 단가표</h2>
      <table border="1">
        <thead>
          <tr>
            <th>평균 원재료수 / 품번수</th>
            {itemOptions.map((item, i) => <th key={i}>{item}</th>)}
          </tr>
        </thead>
        <tbody>
          {originGroupOptions.map((group, rowIdx) => (
            <tr key={rowIdx}>
              <td>{group}</td>
              {originDecisionMatrix[rowIdx].map((price, colIdx) => (
                <td key={colIdx}>{price.toLocaleString()} 원</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>

      <h2>원산지확인서 작성 대행 단가표</h2>
      <table border="1">
        <thead>
          <tr>
            <th>고객사수 / 품번수</th>
            {itemOptions.map((item, i) => <th key={i}>{item}</th>)}
          </tr>
        </thead>
        <tbody>
          {originClientOptions.map((client, rowIdx) => (
            <tr key={rowIdx}>
              <td>{client}</td>
              {originDocMatrix[rowIdx].map((price, colIdx) => (
                <td key={colIdx}>{price.toLocaleString()} 원</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </form>
  );
}
