// Preview.js (Î≥ÄÍ≤ΩÏôÑÎ£å Î≤ÑÏ†Ñ)
import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import jsPDF from "jspdf";
import html2canvas from "html2canvas";
import { HY_BRANCHES, formatDocNo } from "./hyBranches"; // ‚¨ÖÔ∏è Ï∂îÍ∞Ä

export default function Preview() {
  const location = useLocation();
  const navigate = useNavigate();

  const formData = location.state?.formData || {};
  const costs = location.state?.costs || {};
  const branch = HY_BRANCHES[formData.branchKey || "IC"]; // Í∏∞Î≥∏ Ïù∏Ï≤ú

  const today = new Date();
  const dateString = `${String(today.getFullYear()).slice(2)}${String(today.getMonth() + 1).padStart(2, "0")}${String(today.getDate()).padStart(2, "0")}`;

  const downloadPDF = () => {
    const doc = new jsPDF("p", "pt", "a4");
    const input = document.getElementById("invoice-content");

    const companyNameSafe = formData.company ? formData.company.replace(/[^Í∞Ä-Ìû£a-zA-Z0-9\s]/g, "") : "ÏóÖÏ≤¥Î™ÖÏóÜÏùå";
    const filename = `[Í¥ÄÏÑ∏Î≤ïÏù∏ÌïúÏòÅ] ${companyNameSafe} Í≥ºÏÑ∏ÏûêÎ£å Ïª®ÏÑ§ÌåÖ Í≤¨Ï†ÅÏÑú_${dateString}.pdf`;

    html2canvas(input, { scale: 2 }).then((canvas) => {
      const imgData = canvas.toDataURL("image/png");
      const pdfWidth = doc.internal.pageSize.getWidth() - 40;
      const imgProps = doc.getImageProperties(imgData);
      const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;

      doc.addImage(imgData, "PNG", 20, 20, pdfWidth, pdfHeight);
      doc.save(filename);
    });
  };

  const amountMap4 = {
    1: "100Ïñµ Ïù¥Ìïò",
    2: "100Ïñµ Ï¥àÍ≥º 200Ïñµ Ïù¥Ìïò",
    3: "200Ïñµ Ï¥àÍ≥º 300Ïñµ",
    4: "300Ïñµ Ï¥àÍ≥º",
  };

  const reportItemLabels = {
    royalty: "Î°úÏó¥Ìã∞",
    productionSupport: "ÏÉùÏÇ∞ÏßÄÏõêÎπÑ",
    freightInsurance: "Ïö¥ÏûÑ/Î≥¥ÌóòÎ£å",
