import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import jsPDF from "jspdf";
import html2canvas from "html2canvas";

export default function CustomsPreview() {
  const location = useLocation();
  const navigate = useNavigate();

  const { selectedData, company, doc_number } = location.state || {};
  const today = new Date();

  const dateString = `${String(today.getFullYear()).slice(2)}${String(today.getMonth() + 1).padStart(2, "0")}${String(today.getDate()).padStart(2, "0")}`;

  const downloadPDF = () => {
    const doc = new jsPDF("p", "pt", "a4");
    const input = document.getElementById("invoice-content");

    const companyNameSafe = company?.replace(/[^Í∞Ä-Ìû£a-zA-Z0-9\s]/g, "") || "ÏóÖÏ≤¥Î™ÖÏóÜÏùå";
    const filename = `[Í¥ÄÏÑ∏Î≤ïÏù∏ÌïúÏòÅ] ${companyNameSafe} ÌÜµÍ¥Ä ÏàòÏàòÎ£å Í≤¨Ï†ÅÏÑú_${dateString}.pdf`;

    html2canvas(input, { scale: 2 }).then((canvas) => {
      const imgData = canvas.toDataURL("image/png");
      const pdfWidth = doc.internal.pageSize.getWidth() - 40;
      const imgProps = doc.getImageProperties(imgData);
      const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;

      doc.addImage(imgData, "PNG", 20, 20, pdfWidth, pdfHeight);
      doc.save(filename);
    });
  };

  const labelMap = {
    feeRate: "ÏàòÏàòÎ£å ÏöîÏú®",
    minFee: "MIN",
    maxFee: "MAX",
    correctionFee: "Ïã†Í≥†Ï†ïÏ†ï",
    ftaApplication: "FTA ÌòëÏ†ïÏã†Ï≤≠",
    requirementApplication: "ÏöîÍ±¥Ïã†Ï≤≠",
    exportFeeRate: "ÏàòÏàòÎ£å ÏöîÏú®",
    exportMinFee: "MIN",
    exportMaxFee: "MAX",
    exportCorrectionFee: "Ïã†Í≥†Ï†ïÏ†ï",
    exportFtaCertificateAuthorized: "FTA C/O Î∞úÍ∏â (Ïù∏Ï¶ùÏàòÏ∂úÏûê)",
    exportFtaCertificateUnauth: "FTA C/O Î∞úÍ∏â (ÎπÑÏù∏Ï¶ùÏàòÏ∂úÏûê)"
  };

  const importItems = selectedData?.filter(item => !item.label.startsWith('export')) || [];
  const exportItems = selectedData?.filter(item => item.label.startsWith('export')) || [];

  const renderTable = (title, items) => (
    <>
      <h3 style={{ fontSize: 18, margin: "40px 0 10px", color: "#1a2e59" }}>{title}</h3>
      <table style={{ width: "100%", borderCollapse: "collapse", marginBottom: 40, fontSize: 14 }}>
     <thead>
  <tr style={{ borderBottom: "1px solid #ccc", backgroundColor: "#f9f9f9" }}>
    <th style={{ padding: "12px 8px", width: "35%", textAlign: "center", verticalAlign: "middle" }}>Ìï≠Î™©</th>
    <th style={{ padding: "12px 8px", width: "25%", textAlign: "center", verticalAlign: "middle" }}>Í∏àÏï°</th>
    <th style={{ padding: "12px 8px", width: "40%", textAlign: "center", verticalAlign: "middle" }}>ÎπÑÍ≥†</th>
  </tr>
</thead>
<tbody>
  {items.map((item, idx) => (
    <tr key={idx} style={{ borderBottom: "1px solid #eee" }}>
      <td style={{ padding: "12px 8px", textAlign: "center", verticalAlign: "middle" }}>{labelMap[item.label] || item.label}</td>
      <td style={{ padding: "12px 8px", textAlign: "center", verticalAlign: "middle" }}>
        {item.label.includes("Rate")
          ? `${item.value}%`
          : `${Number(item.value).toLocaleString()} Ïõê`}
      </td>
      <td style={{ padding: "12px 8px", textAlign: "center", verticalAlign: "middle" }}>
        {item.note || "-"}
      </td>
    </tr>
  ))}
</tbody>

      </table>
    </>
  );

  return (
    <>
      <div
        id="invoice-content"
        style={{
          fontFamily: "'Pretendard', 'Helvetica Neue', sans-serif",
          backgroundColor: "#fff",
          color: "#222",
          padding: 60,
          maxWidth: 800,
          margin: "60px auto",
          lineHeight: 1.7,
          border: "1px solid #ddd",
          minHeight: "1122px",
          boxSizing: "border-box",
          display: "flex",
          flexDirection: "column",
          justifyContent: "space-between"
        }}
      >
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: 60 }}>
          <div>
            <h1 style={{ fontSize: 40, fontWeight: 700, marginBottom: 10 }}>Invoice</h1>
            <p style={{ fontSize: 14, color: "#555" }}>
              No. HY25{doc_number || "XXXX"} / {today.toLocaleDateString("ko-KR")}
            </p>
          </div>
          <div style={{ width: 170 }}>
            <img
              src={`${process.env.PUBLIC_URL}/images/HYLOGO_NAVY.png`}
              alt="ÌïúÏòÅ Î°úÍ≥†"
              style={{ width: "100%", height: "auto", objectFit: "contain" }}
            />
          </div>
        </div>

        <div style={{ display: "flex", justifyContent: "space-between", marginBottom: 40 }}>
          <div>
            <strong>Client</strong>
            <p style={{ fontSize: 14 }}>{company || "Client Name"}</p>
          </div>
          <div style={{ textAlign: "right" }}>
            <strong>Issued By</strong>
            <p style={{ fontSize: 14 }}>Í¥ÄÏÑ∏Î≤ïÏù∏ ÌïúÏòÅ</p>
          </div>
        </div>

        {renderTable("ÏàòÏûÖÌÜµÍ¥Ä ÏàòÏàòÎ£å", importItems)}
        {renderTable("ÏàòÏ∂úÌÜµÍ¥Ä ÏàòÏàòÎ£å", exportItems)}

        <div style={{ display: "flex", justifyContent: "space-between", fontSize: 14 }}>
          <div>
            <p><strong>Bank:</strong> Ïã†ÌïúÏùÄÌñâ</p>
            <p><strong>Account:</strong> 140-015-193246</p>
            <p><strong>Holder:</strong> Í¥ÄÏÑ∏Î≤ïÏù∏ ÌïúÏòÅ Ïù∏Ï≤úÏßÄÏÇ¨</p>
          </div>
          <div style={{ textAlign: "right", fontSize: 13, color: "#666" }}>
            <p><strong>Valid Until:</strong> {new Date(today.getTime() + 15 * 86400000).toLocaleDateString("ko-KR")}</p>
            <p>Ïù∏Ï≤úÍ¥ëÏó≠Ïãú ÏÑúÍµ¨ Ïù¥Ïùå4Î°ú6 KRÎ≤ïÏ°∞ÌÉÄÏõå 915Ìò∏</p>
            <p>TEL: 032-713-4897 / FAX: 032-713-4898</p>
          </div>
        </div>
      </div>

      <div style={{ marginTop: 40, display: "flex", justifyContent: "center", gap: 20 }}>
        <button
          onClick={() => navigate("/")}
          style={{
            backgroundColor: "#aaa",
            border: "none",
            padding: "10px 30px",
            borderRadius: 6,
            cursor: "pointer",
            fontWeight: "600",
            color: "#fff"
          }}
        >
          Îã§ÏãúÌïòÍ∏∞
        </button>
        <button
          onClick={downloadPDF}
          style={{
            backgroundColor: "#111",
            border: "none",
            padding: "10px 30px",
            borderRadius: 6,
            cursor: "pointer",
            fontWeight: "700",
            color: "#fff"
          }}
        >
          üì• PDF Îã§Ïö¥Î°úÎìú
        </button>
      </div>
    </>
  );
}
