import React, { useState, useEffect, useMemo } from "react";
import { useNavigate } from "react-router-dom";

export default function QuotationFTA() {
  const navigate = useNavigate();

  const ftaMatrix = useMemo(() => [
    [30000, 25000, 20000, 15000, 10000, 5000, 3000],
    [null, 30000, 25000, 20000, 15000, 10000, 5000],
    [null, 35000, 30000, 25000, 20000, 15000, 10000],
    [null, 40000, 35000, 30000, 25000, 20000, 15000],
    [null, null, 40000, 35000, 30000, 25000, 20000],
    [null, null, 45000, 40000, 35000, 30000, 25000],
  ], []);

  const originMatrix = useMemo(() => [
    [15000, 12000, 10000, 8000, 5000, 3000, 2000],
    [18000, 15000, 12000, 10000, 8000, 5000, 3000],
    [20000, 18000, 15000, 12000, 10000, 8000, 5000],
    [22000, 20000, 18000, 15000, 12000, 10000, 8000],
  ], []);

  const groupOptions = [
    "10Í∞ú Ïù¥Ìïò",
    "10Í∞ú Ï¥àÍ≥º 30Í∞ú Ïù¥Ìïò",
    "30Í∞ú Ï¥àÍ≥º 60Í∞ú Ïù¥Ìïò",
    "60Í∞ú Ï¥àÍ≥º 100Í∞ú Ïù¥Ìïò",
    "100Í∞ú Ï¥àÍ≥º 200Í∞ú Ïù¥Ìïò",
    "200Í∞ú Ï¥àÍ≥º"
  ];

  const itemOptions = [
    "10Í∞ú Ïù¥Ìïò",
    "10Í∞ú Ï¥àÍ≥º 100Í∞ú Ïù¥Ìïò",
    "100Í∞ú Ï¥àÍ≥º 500Í∞ú Ïù¥Ìïò",
    "500Í∞ú Ï¥àÍ≥º 1000Í∞ú Ïù¥Ìïò",
    "1000Í∞ú Ï¥àÍ≥º 3000Í∞ú Ïù¥Ìïò",
    "3000Í∞ú Ï¥àÍ≥º 10000Í∞ú Ïù¥Ìïò",
    "10000Í∞ú Ï¥àÍ≥º"
  ];

  const originGroupOptions = ["10Í∞ú Ïù¥Ìïò", "30Í∞ú Ïù¥Ìïò", "60Í∞ú Ïù¥Ìïò", "60Í∞ú Ï¥àÍ≥º"];
  const originItemOptions = itemOptions;

  const [form, setForm] = useState({
    company: "",
    doc_number: "",
    groupIndex: 0,
    itemIndex: 0,
    originGroupIndex: 0,
    originItemIndex: 0,
    qty: 1,
    discountRate: 0,
    note: "",
    includeFTA: false,
    includeOrigin: false,
  });

  const [costs, setCosts] = useState({
    unitPrice: 0,
    totalAmount: 0,
    discount: 0,
    vat: 0,
    total: 0,
  });

  useEffect(() => {
    let unit = 0;
    if (form.includeFTA) {
      unit += ftaMatrix[form.groupIndex][form.itemIndex] ?? 0;
    }
    if (form.includeOrigin) {
      unit += originMatrix[form.originGroupIndex][form.originItemIndex] ?? 0;
    }
    const raw = unit * form.qty;
    const discount = Math.round(raw * (form.discountRate / 100));
    const vat = Math.round((raw - discount) * 0.1);
    const total = raw - discount + vat;

    setCosts({ unitPrice: unit, totalAmount: raw, discount, vat, total });
  }, [form, ftaMatrix, originMatrix]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : ["qty", "discountRate"].includes(name) ? Number(value) : value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const selectedData = [];
    if (form.includeFTA) {
      selectedData.push({
        label: "ÌíàÎ™©Î∂ÑÎ•ò",
        detail: `ÌíàÎ™©Íµ∞Ïàò: ${groupOptions[form.groupIndex]}, ÌíàÎ≤àÏàò: ${itemOptions[form.itemIndex]}`,
        qty: form.qty,
        value: ftaMatrix[form.groupIndex][form.itemIndex] ?? 0,
        note: form.note,
      });
    }
    if (form.includeOrigin) {
      selectedData.push({
        label: "ÏõêÏÇ∞ÏßÄ ÌåêÏ†ï",
        detail: `ÌèâÍ∑† ÏõêÏû¨Î£åÏàò: ${originGroupOptions[form.originGroupIndex]}, ÌíàÎ≤àÏàò: ${originItemOptions[form.originItemIndex]}`,
        qty: form.qty,
        value: originMatrix[form.originGroupIndex][form.originItemIndex] ?? 0,
        note: form.note,
      });
    }
    navigate("/preview", {
      state: {
        formData: {
          company: form.company,
          doc_number: form.doc_number,
          discountRate: form.discountRate,
          note: form.note,
        },
        costs: {
          discountAmount: costs.discount,
          vat: costs.vat,
          totalCost: costs.total,
        },
        selectedData,
      },
    });
  };

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: 900, margin: "0 auto", padding: 30 }}>
      <h2>FTA ÌíàÎ™©Î∂ÑÎ•ò Í≤¨Ï†ÅÏÑú</h2>
      {/* ÏÉùÎûµÎêú ÏûÖÎ†• ÏòÅÏó≠ */}
      <table>
        <tbody>
          <tr>
            <td><input type="checkbox" name="includeFTA" checked={form.includeFTA} onChange={handleChange} /></td>
            <td>ÌíàÎ™©Î∂ÑÎ•ò</td>
            <td>
              <select name="groupIndex" value={form.groupIndex} onChange={handleChange}>
                {groupOptions.map((opt, idx) => <option value={idx} key={idx}>{opt}</option>)}
              </select>
              <select name="itemIndex" value={form.itemIndex} onChange={handleChange}>
                {itemOptions.map((opt, idx) => <option value={idx} key={idx}>{opt}</option>)}
              </select>
            </td>
            <td>
              <input type="number" name="qty" value={form.qty} onChange={handleChange} min={1} />
            </td>
            <td>{form.includeFTA ? (ftaMatrix[form.groupIndex][form.itemIndex] * form.qty).toLocaleString() : ""}</td>
          </tr>

          <tr>
            <td><input type="checkbox" name="includeOrigin" checked={form.includeOrigin} onChange={handleChange} /></td>
            <td>ÏõêÏÇ∞ÏßÄ ÌåêÏ†ï</td>
            <td>
              <select name="originGroupIndex" value={form.originGroupIndex} onChange={handleChange}>
                {originGroupOptions.map((opt, idx) => <option value={idx} key={idx}>{opt}</option>)}
              </select>
              <select name="originItemIndex" value={form.originItemIndex} onChange={handleChange}>
                {originItemOptions.map((opt, idx) => <option value={idx} key={idx}>{opt}</option>)}
              </select>
            </td>
            <td>
              <input type="number" name="qty" value={form.qty} onChange={handleChange} min={1} />
            </td>
            <td>{form.includeOrigin ? (originMatrix[form.originGroupIndex][form.originItemIndex] * form.qty).toLocaleString() : ""}</td>
          </tr>
        </tbody>
      </table>

      {/* Ìï†Ïù∏ Î∞è Ìï©Í≥Ñ */}
      <div style={{ textAlign: "right", lineHeight: 2, fontSize: 15, marginBottom: 30 }}>
        <div>
          Ìï†Ïù∏Ïú®:
          <input
            type="number"
            name="discountRate"
            value={form.discountRate}
            onChange={handleChange}
