import { useNavigate } from "react-router-dom";
import React, { useState, useEffect, useCallback } from "react";

const baseMatrix = [
  [300000, 400000, 500000, 600000, 700000],
  [400000, 500000, 600000, 700000, 800000],
  [600000, 700000, 800000, 900000, 1000000],
  [800000, 900000, 1000000, 1100000, 1200000],
];

const relatedMatrix = [
  [3000000, 4000000, 5000000, 6000000, 7000000],
  [4000000, 5000000, 6000000, 7000000, 8000000],
  [5000000, 6000000, 7000000, 8000000, 9000000],
  [6000000, 7000000, 8000000, 9000000, 10000000],
];

export default function QuoteForm() {
  const navigate = useNavigate();
  const [state, setState] = useState({
    company: "",
    doc_number: "",
    includeBase: false,
    partners: 0,
    importAmount: 1,
    includeReason: false,
    reasonType: "basic",
    missingItems: 0,
    includeDoc: false,
    docPartners: 0,
    includeReport: false,
    reportItems: {
      royalty: false,
      productionSupport: false,
      commission: false,
      freightInsurance: false,
      packaging: false,
      postBenefit: false,
      indirectPayment: false,
      other: false,
    },
    includeRelated: false,
    relatedPartners: 0,
    relatedImportAmount: 1,
    note: "",
    discountRate: 0,
  });

  const [costs, setCosts] = useState({
    baseCost: 0,
    reasonCost: 0,
    docCost: 0,
    reportCost: 0,
    relatedCost: 0,
    subTotal: 0,
    discountAmount: 0,
    vat: 0,
    totalCost: 0,
  });

  const handleChange = (e) => {
    const { name, type, checked, value } = e.target;
    if (type === "checkbox" && name.startsWith("reportItems")) {
      const key = name.split(".")[1];
      setState((prev) => ({
        ...prev,
        reportItems: { ...prev.reportItems, [key]: checked },
      }));
    } else if (type === "checkbox") {
      setState((prev) => ({ ...prev, [name]: checked }));
    } else {
      setState((prev) => ({
        ...prev,
        [name]: type === "number" ? Number(value) : value,
      }));
    }
  };

  const calculateCosts = useCallback(() => {
    let subTotal = 0;
    let baseCost = 0,
      reasonCost = 0,
      docCost = 0,
      reportCost = 0,
      relatedCost = 0;

    if (state.includeBase) {
      const row = state.partners <= 10 ? 0 : state.partners <= 30 ? 1 : state.partners <= 100 ? 2 : 3;
      baseCost = baseMatrix[row][state.importAmount - 1];
      subTotal += baseCost;
    }

    if (state.includeReason) {
      reasonCost = state.reasonType === "basic"
        ? 100000 * (state.reasonPartners || 0)
        : 100000 * (state.reasonPartners || 0) + 30000 * (state.missingItems || 0);
      subTotal += reasonCost;
    }

    if (state.includeDoc) {
      docCost = state.docPartners * 50000;
      subTotal += docCost;
    }

    if (state.includeReport) {
      const checkedCount = Object.values(state.reportItems).filter(Boolean).length;
      reportCost = checkedCount * (state.reportPartners || 0) * 1000000;
      subTotal += reportCost;
    }

    if (state.includeRelated) {
      const row = state.relatedPartners <= 10 ? 0 : state.relatedPartners <= 30 ? 1 : state.relatedPartners <= 50 ? 2 : 3;
      relatedCost = relatedMatrix[row][state.relatedImportAmount - 1];
      subTotal += relatedCost;
    }

    const discountAmount = Math.round(subTotal * (state.discountRate / 100));
    const discountedSubTotal = subTotal - discountAmount;
    const vat = Math.round(discountedSubTotal * 0.1);
    const totalCost = discountedSubTotal + vat;

    setCosts({
      baseCost,
      reasonCost,
      docCost,
      reportCost,
      relatedCost,
      subTotal,
      discountAmount,
      vat,
      totalCost,
    });
  }, [state]);

  useEffect(() => {
    calculateCosts();
  }, [calculateCosts]);

  const handleSubmit = (e) => {
    e.preventDefault();
    calculateCosts();
    navigate("/preview", {
      state: {
        formData: state,
        costs,
      },
    });
  };

  return (
    <form
      onSubmit={handleSubmit}
      style={{
        fontFamily: "'Pretendard', sans-serif",
        backgroundColor: "#fff",
        color: "#111",
        padding: "40px 60px",
        maxWidth: 960,
        margin: "40px auto",
        boxShadow: "0 8px 30px rgba(0, 0, 0, 0.05)",
        borderRadius: 16,
        border: "1px solid #eaeaea",
      }}
    >
      <h1 style={{ fontSize: 28, fontWeight: 700, marginBottom: 30 }}>Í≤¨Ï†ÅÏÑú ÏÉùÏÑ±</h1>

      <div style={{ marginBottom: 20 }}>
        <label htmlFor="company" style={{ fontWeight: 600, marginBottom: 8, display: "block" }}>
          ÌöåÏÇ¨Î™Ö
        </label>
        <input
          type="text"
          id="company"
          name="company"
          value={state.company}
          onChange={handleChange}
          placeholder="ÌöåÏÇ¨Î™ÖÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
          style={{
            width: "100%",
            padding: "10px 14px",
            fontSize: 15,
            borderRadius: 8,
            border: "1.5px solid #ccc",
            boxSizing: "border-box",
          }}
        />
      </div>

      <div style={{ marginBottom: 20 }}>
        <label htmlFor="doc_number" style={{ fontWeight: 600, marginBottom: 8, display: "block" }}>
          Î¨∏ÏÑúÎ≤àÌò∏
        </label>
        <input
          type="text"
          id="doc_number"
          name="doc_number"
          value={state.doc_number}
          onChange={handleChange}
          placeholder="Î¨∏ÏÑúÎ≤àÌò∏ ÏûÖÎ†•"
          style={{
            width: "100%",
            padding: "10px 14px",
            fontSize: 15,
            borderRadius: 8,
            border: "1.5px solid #ccc",
            boxSizing: "border-box",
          }}
        />
      </div>

      <button
        type="submit"
        style={{
          backgroundColor: "#111",
          color: "#fff",
          padding: "12px 28px",
          fontSize: 16,
          fontWeight: 600,
          border: "none",
          borderRadius: 8,
          cursor: "pointer",
        }}
        onMouseOver={(e) => (e.currentTarget.style.backgroundColor = "#444")}
        onMouseOut={(e) => (e.currentTarget.style.backgroundColor = "#111")}
      >
        üìÑ Í≤¨Ï†ÅÏÑú ÏÉùÏÑ±
      </button>
    </form>
  );
}
