import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import jsPDF from "jspdf";
import html2canvas from "html2canvas";

export default function Preview() {
  const location = useLocation();
  const navigate = useNavigate();

  // Ïù¥Ï†Ñ ÌéòÏù¥ÏßÄÏóêÏÑú Ï†ÑÎã¨Ìïú Îç∞Ïù¥ÌÑ∞
  const formData = location.state?.formData || {};
  const costs = location.state?.costs || {};

  // Ïò§Îäò ÎÇ†Ïßú YYMMDD Ìè¨Îß∑
  const today = new Date();
  const dateString = `${String(today.getFullYear()).slice(2)}${String(today.getMonth() + 1).padStart(2, "0")}${String(today.getDate()).padStart(2, "0")}`;

  // PDF Îã§Ïö¥Î°úÎìú Ìï®Ïàò
  const downloadPDF = () => {
    const doc = new jsPDF("p", "pt", "a4");
    const input = document.getElementById("invoice-content");

    const companyNameSafe = formData.company ? formData.company.replace(/[^Í∞Ä-Ìû£a-zA-Z0-9\s]/g, "") : "ÏóÖÏ≤¥Î™ÖÏóÜÏùå";
    const filename = `[Í¥ÄÏÑ∏Î≤ïÏù∏ÌïúÏòÅ] ${companyNameSafe} Í≥ºÏÑ∏ÏûêÎ£å Ïª®ÏÑ§ÌåÖ Í≤¨Ï†ÅÏÑú_${dateString}.pdf`;

    html2canvas(input, { scale: 2 }).then((canvas) => {
      const imgData = canvas.toDataURL("image/png");
      const pdfWidth = doc.internal.pageSize.getWidth() - 40; // Ï¢åÏö∞ Ïó¨Î∞± 20Ïî©
      const imgProps = doc.getImageProperties(imgData);
      const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;

      doc.addImage(imgData, "PNG", 20, 20, pdfWidth, pdfHeight);
      doc.save(filename);
    });
  };

  // ÏÑúÎπÑÏä§ Ìï≠Î™© Î∞∞Ïó¥ (ÎèôÏùºÌïú ÏàúÏÑú Î∞è Î™ÖÏπ≠ÏúºÎ°ú Ï∂úÎ†•)
  const serviceItems = [
    { label: "Í±∞ÎûòÍµ¨Ï°∞ Î∞è Í≥ºÏÑ∏ÏûêÎ£å Ï†úÏ∂ú Î≤îÏúÑ Í≤ÄÌÜ†", checked: formData.includeBase, price: costs.baseCost },
    { label: "Í≥ºÏÑ∏ÏûêÎ£å ÎØ∏Ï†úÏ∂ú ÏÇ¨Ïú†ÏÑú ÏûëÏÑ± ÏßÄÏõê", checked: formData.includeReason, price: costs.reasonCost },
    { label: "Í≥ºÏÑ∏ÏûêÎ£å Ï†úÏ∂ú ÏÑúÎ•ò Ï†ïÎπÑ Î∞è Ï†ÑÏûêÏ†úÏ∂ú ÎåÄÌñâ", checked: formData.includeDoc, price: costs.docCost },
    { label: "Í≥ºÏÑ∏Í∞ÄÍ≤© Í≤∞Ï†ï ÏÇ¨Ïú† Î≥¥Í≥†ÏÑú ÏûëÏÑ±", checked: formData.includeReport, price: costs.reportCost },
    { label: "ÌäπÏàòÍ¥ÄÍ≥ÑÏûê Í±∞Îûò Î∂ÑÏÑù Î∞è Ïù¥Ï†ÑÍ∞ÄÍ≤© Í≤ÄÌÜ† Î¶¨Ìè¨Ìä∏", checked: formData.includeRelated, price: costs.relatedCost },
  ];

  // ÏÑ†ÌÉùÎêú ÏÑúÎπÑÏä§Îßå ÌïÑÌÑ∞ÎßÅ
  const selectedServices = serviceItems.filter((item) => item.checked);

return (
  <div>
    {/* Ïù∏Î≥¥Ïù¥Ïä§ ÎÇ¥Ïö©Îßå Ïù¥ divÏóê */}
    <div
      id="invoice-content"
      style={{
        fontFamily: "'Pretendard', 'Segoe UI', 'Helvetica Neue', sans-serif",
        backgroundColor: "#fff",
        color: "#222",
        padding: 40,
        maxWidth: 700,
        margin: "40px auto",
        lineHeight: 1.6,
        boxShadow: "0 0 15px rgba(0,0,0,0.1)",
        borderRadius: 8,
      }}
    >
      {/* ...Ïù∏Î≥¥Ïù¥Ïä§ ÎÇ¥Ïö©... */}
    </div>

    {/* Î≤ÑÌäº ÏòÅÏó≠ÏùÄ Î≥ÑÎèÑÏùò divÎ°ú Î∂ÑÎ¶¨ */}
    <div style={{ marginTop: 40, display: "flex", justifyContent: "center", gap: 20 }}>
      <button
        onClick={() => navigate("/")}
        style={{
          backgroundColor: "#aaa",
          border: "none",
          padding: "10px 30px",
          borderRadius: 8,
          cursor: "pointer",
          fontWeight: "600",
          color: "#fff",
        }}
      >
        Îã§ÏãúÌïòÍ∏∞
      </button>

      <button
        onClick={downloadPDF}
        style={{
          backgroundColor: "#3f72af",
          border: "none",
          padding: "10px 30px",
          borderRadius: 8,
          cursor: "pointer",
          fontWeight: "700",
          color: "#fff",
          boxShadow: "0 3px 7px rgba(63, 114, 175, 0.6)",
        }}
      >
        üì• PDF Îã§Ïö¥Î°úÎìú
      </button>
    </div>
  </div>
);

}
