import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import jsPDF from "jspdf";
import html2canvas from "html2canvas";

export default function Preview() {
  const location = useLocation();
  const navigate = useNavigate();

  // Ïù¥Ï†Ñ ÌéòÏù¥ÏßÄÏóêÏÑú Ï†ÑÎã¨Ìïú Îç∞Ïù¥ÌÑ∞
  const formData = location.state?.formData || {};
  const costs = location.state?.costs || {};

  // Ïò§Îäò ÎÇ†Ïßú YYMMDD Ìè¨Îß∑
  const today = new Date();
  const dateString = `${String(today.getFullYear()).slice(2)}${String(
    today.getMonth() + 1
  ).padStart(2, "0")}${String(today.getDate()).padStart(2, "0")}`;

  // PDF Îã§Ïö¥Î°úÎìú Ìï®Ïàò
  const downloadPDF = () => {
    const doc = new jsPDF("p", "pt", "a4");
    const input = document.getElementById("invoice-content");

    const companyNameSafe = formData.company
      ? formData.company.replace(/[^Í∞Ä-Ìû£a-zA-Z0-9\s]/g, "")
      : "ÏóÖÏ≤¥Î™ÖÏóÜÏùå";
    const filename = `[Í¥ÄÏÑ∏Î≤ïÏù∏ÌïúÏòÅ] ${companyNameSafe} Í≥ºÏÑ∏ÏûêÎ£å Ïª®ÏÑ§ÌåÖ Í≤¨Ï†ÅÏÑú_${dateString}.pdf`;

    html2canvas(input, { scale: 2 }).then((canvas) => {
      const imgData = canvas.toDataURL("image/png");
      const pdfWidth = doc.internal.pageSize.getWidth() - 40; // Ï¢åÏö∞ Ïó¨Î∞± 20Ïî©
      const imgProps = doc.getImageProperties(imgData);
      const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;

      doc.addImage(imgData, "PNG", 20, 20, pdfWidth, pdfHeight);
      doc.save(filename);
    });
  };

  const serviceItems = [
    {
      label: "Í±∞ÎûòÍµ¨Ï°∞ Î∞è Í≥ºÏÑ∏ÏûêÎ£å Ï†úÏ∂ú Î≤îÏúÑ Í≤ÄÌÜ†",
      checked: formData.includeBase,
      price: costs.baseCost,
    },
    {
      label: "Í≥ºÏÑ∏ÏûêÎ£å ÎØ∏Ï†úÏ∂ú ÏÇ¨Ïú†ÏÑú ÏûëÏÑ± ÏßÄÏõê",
      checked: formData.includeReason,
      price: costs.reasonCost,
    },
    {
      label: "Í≥ºÏÑ∏ÏûêÎ£å Ï†úÏ∂ú ÏÑúÎ•ò Ï†ïÎπÑ Î∞è Ï†ÑÏûêÏ†úÏ∂ú ÎåÄÌñâ",
      checked: formData.includeDoc,
      price: costs.docCost,
    },
    {
      label: "Í≥ºÏÑ∏Í∞ÄÍ≤© Í≤∞Ï†ï ÏÇ¨Ïú† Î≥¥Í≥†ÏÑú ÏûëÏÑ±",
      checked: formData.includeReport,
      price: costs.reportCost,
    },
    {
      label: "ÌäπÏàòÍ¥ÄÍ≥ÑÏûê Í±∞Îûò Î∂ÑÏÑù Î∞è Ïù¥Ï†ÑÍ∞ÄÍ≤© Í≤ÄÌÜ† Î¶¨Ìè¨Ìä∏",
      checked: formData.includeRelated,
      price: costs.relatedCost,
    },
  ];

  const selectedServices = serviceItems.filter((item) => item.checked);

  return (
    <>
      {/* Ïù∏Î≥¥Ïù¥Ïä§ ÎÇ¥Ïö© ÏòÅÏó≠: PDF Ï∫°Ï≥ê ÎåÄÏÉÅ */}
      <div
        id="invoice-content"
        style={{
          fontFamily: "'Pretendard', 'Segoe UI', 'Helvetica Neue', sans-serif",
          backgroundColor: "#fff",
          color: "#222",
          padding: 40,
          maxWidth: 700,
          margin: "40px auto",
          lineHeight: 1.6,
          boxShadow: "0 0 15px rgba(0,0,0,0.1)",
          borderRadius: 8,
        }}
      >
        {/* Ïù∏Î≥¥Ïù¥Ïä§ Ìó§Îçî */}
        <div className="invoice-header" style={{ marginBottom: 30 }}>
          <div
            style={{
              fontSize: 14,
              letterSpacing: 1.5,
              color: "#666",
              textTransform: "uppercase",
              fontWeight: 500,
            }}
          >
            THE HANYOUNG CUSTOMS SERVICES
          </div>
          <h1
            style={{
              fontSize: 48,
              fontWeight: 700,
              letterSpacing: 2,
              margin: "5px 0 0",
              color: "#111",
            }}
          >
            INVOICE
          </h1>
        </div>

        {/* ÏàòÏã†/Î∞úÏã† ÌÖåÏù¥Î∏î */}
        <table
          className="meta"
          style={{ width: "100%", marginBottom: 20, fontSize: 14, borderCollapse: "collapse" }}
        >
          <tbody>
            <tr>
              <td style={{ padding: "8px 0", fontWeight: "600", width: 60 }}>ÏàòÏã†</td>
              <td>{formData.company || "-"}</td>
              <td style={{ padding: "8px 0", fontWeight: "600", width: 60 }}>Î∞úÏã†</td>
              <td>Í¥ÄÏÑ∏Î≤ïÏù∏ ÌïúÏòÅ</td>
            </tr>
            <tr>
              <td style={{ padding: "8px 0", fontWeight: "600" }}>ÏùºÏûê</td>
              <td>{today.toLocaleDateString("ko-KR")}</td>
              <td style={{ padding: "8px 0", fontWeight: "600" }}>Î¨∏ÏÑúÎ≤àÌò∏</td>
              <td>{formData.doc_number || "-"}</td>
            </tr>
          </tbody>
        </table>

        {/* Î©îÏÑ∏ÏßÄ */}
        <p style={{ fontSize: 13, color: "#666", marginBottom: 40 }}>
          ÌïúÏòÅÏù¥ÎùºÎäî Ïù¥Î¶ÑÏù¥ Í∏∞ÏóÖÏóêÍ≤å ÏûêÏÇ∞Ïù¥ Îê† Ïàò ÏûàÎèÑÎ°ù,
          <br />
          Ïñ∏Ï†úÎÇò Í≥†Í∞ùÏùò Í∏∞ÎåÄÎ•º Îõ∞Ïñ¥ÎÑòÎäî Í¥ÄÏÑ∏Î≤ïÏù∏ ÌïúÏòÅÏù¥ ÎêòÍ≤†ÏäµÎãàÎã§.
        </p>

        {/* ÏÑúÎπÑÏä§ ÎÇ¥Ïó≠ ÌÖåÏù¥Î∏î */}
        <table
          style={{ width: "100%", borderCollapse: "collapse", marginBottom: 40, fontSize: 14 }}
        >
          <thead>
            <tr style={{ backgroundColor: "#f0f0f0" }}>
              <th style={{ border: "1px solid #ddd", padding: 12, fontWeight: 600 }}>NO</th>
              <th style={{ border: "1px solid #ddd", padding: 12, fontWeight: 600 }}>ÏÉÅÏÑ∏</th>
              <th
                style={{
                  border: "1px solid #ddd",
                  padding: 12,
                  fontWeight: 600,
                  width: 80,
                  textAlign: "center",
                }}
              >
                ÏàòÎüâ
              </th>
              <th
                style={{
                  border: "1px solid #ddd",
                  padding: 12,
                  fontWeight: 600,
                  width: 120,
                  textAlign: "right",
                }}
              >
                Îã®Í∞Ä (Ïõê)
              </th>
              <th
                style={{
                  border: "1px solid #ddd",
                  padding: 12,
                  fontWeight: 600,
                  width: 140,
                  textAlign: "right",
                }}
              >
                Í∏àÏï° (Ïõê)
              </th>
            </tr>
          </thead>
          <tbody>
            {selectedServices.length === 0 && (
              <tr>
                <td colSpan={5} style={{ padding: 12, textAlign: "center" }}>
                  ÏÑ†ÌÉùÎêú ÏÑúÎπÑÏä§Í∞Ä ÏóÜÏäµÎãàÎã§.
                </td>
              </tr>
            )}
            {selectedServices.map((item, idx) => (
              <tr key={idx}>
                <td style={{ border: "1px solid #ddd", padding: 12 }}>{idx + 1}</td>
                <td style={{ border: "1px solid #ddd", padding: 12 }}>{item.label}</td>
                <td style={{ border: "1px solid #ddd", padding: 12, textAlign: "center" }}>1</td>
                <td style={{ border: "1px solid #ddd", padding: 12, textAlign: "right" }}>
                  {item.price.toLocaleString()}
                </td>
                <td style={{ border: "1px solid #ddd", padding: 12, textAlign: "right" }}>
                  {item.price.toLocaleString()}
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {/* Ìï©Í≥Ñ */}
        <div
          style={{
            textAlign: "right",
            fontSize: 18,
            fontWeight: 700,
            color: "#000",
            marginBottom: 20,
          }}
        >
          Ìï©Í≥Ñ : {costs.totalCost.toLocaleString()} Ïõê
        </div>

        {/* Í≥ÑÏ¢åÏ†ïÎ≥¥ */}
        <table style={{ width: "100%", fontSize: 14, marginBottom: 30 }}>
          <tbody>
            <tr>
              <td style={{ width: 80, fontWeight: "600" }}>ÏùÄÌñâÎ™Ö</td>
              <td>Ïã†ÌïúÏùÄÌñâ</td>
            </tr>
            <tr>
              <td style={{ fontWeight: "600" }}>Í≥ÑÏ¢åÎ≤àÌò∏</td>
              <td>140-015-193246</td>
            </tr>
            <tr>
              <td style={{ fontWeight: "600" }}>ÏòàÍ∏àÏ£º</td>
              <td>Í¥ÄÏÑ∏Î≤ïÏù∏ ÌïúÏòÅ Ïù∏Ï≤úÏßÄÏÇ¨</td>
            </tr>
          </tbody>
        </table>

        {/* Í≤¨Ï†Å Ïú†Ìö®Í∏∞Í∞Ñ Î∞è Ï£ºÏÜå */}
        <div style={{ fontSize: 12, color: "#666" }}>
          <p>
            <strong>Í≤¨Ï†Å Ïú†Ìö®Í∏∞Í∞Ñ:</strong>{" "}
            {new Date(today.getTime() + 15 * 24 * 60 * 60 * 1000).toLocaleDateString(
              "ko-KR"
            )}
          </p>
          <p>
            Ïù∏Ï≤úÍ¥ëÏó≠Ïãú ÏÑúÍµ¨ Ïù¥Ïùå4Î°ú6 KRÎ≤ïÏ°∞ÌÉÄÏõå 915Ìò∏
            <br />
            (Ï†ÑÌôî) 032-713-4897 | (Ìå©Ïä§) 032-713-4898
          </p>
        </div>
      </div>

      {/* Î≤ÑÌäº ÏòÅÏó≠: Ïù∏Î≥¥Ïù¥Ïä§ ÏòÅÏó≠ Î∞îÍπ•Ïóê ÏúÑÏπò */}
      <div
        style={{
          marginTop: 40,
          display: "flex",
          justifyContent: "center",
          gap: 20,
        }}
      >
        <button
          onClick={() => navigate("/")}
          style={{
            backgroundColor: "#aaa",
            border: "none",
            padding: "10px 30px",
            borderRadius: 8,
            cursor: "pointer",
            fontWeight: "600",
            color: "#fff",
          }}
        >
          Îã§ÏãúÌïòÍ∏∞
        </button>

        <button
          onClick={downloadPDF}
          style={{
            backgroundColor: "#3f72af",
            border: "none",
            padding: "10px 30px",
            borderRadius: 8,
            cursor: "pointer",
            fontWeight: "700",
            color: "#fff",
            boxShadow: "0 3px 7px rgba(63, 114, 175, 0.6)",
          }}
        >
          üì• PDF Îã§Ïö¥Î°úÎìú
        </button>
      </div>
    </>
  );
}
