import React, { useState } from "react";

const initialState = {
  includeBase: false,
  partners: 10,
  importAmount: 1,
  includeReason: false,
  reasonType: "basic",
  missingItems: 0,
  includeDoc: false,
  docPartners: 10,
  includeReport: false,
  reportItems: {
    royalty: false,
    productionSupport: false,
    commission: false,
    freightInsurance: false,
    packaging: false,
    postBenefit: false,
    indirectPayment: false,
  },
  includeRelated: false,
  relatedPartners: 10,
  relatedImportAmount: 1,
};

export default function QuoteForm() {
  const [state, setState] = useState(initialState);
  const [costs, setCosts] = useState({
    baseCost: 0,
    reasonCost: 0,
    docCost: 0,
    reportCost: 0,
    relatedCost: 0,
    totalCost: 0,
  });

  const baseMatrix = [
    [300000, 400000, 500000, 600000, 700000],
    [400000, 500000, 600000, 700000, 800000],
    [600000, 700000, 800000, 900000, 1000000],
    [800000, 900000, 1000000, 1100000, 1200000],
  ];

  const relatedMatrix = [
    [3000000, 4000000, 5000000, 6000000, 7000000],
    [4000000, 5000000, 6000000, 7000000, 8000000],
    [5000000, 6000000, 7000000, 8000000, 9000000],
    [6000000, 7000000, 8000000, 9000000, 10000000],
  ];

  const handleChange = (e) => {
    const { name, type, checked, value } = e.target;
    if (type === "checkbox" && name.startsWith("reportItems")) {
      const key = name.split(".")[1];
      setState((prev) => ({
        ...prev,
        reportItems: {
          ...prev.reportItems,
          [key]: checked,
        },
      }));
    } else if (type === "checkbox") {
      setState((prev) => ({
        ...prev,
        [name]: checked,
      }));
    } else {
      setState((prev) => ({
        ...prev,
        [name]: type === "number" ? Number(value) : value,
      }));
    }
  };

  const calculateCosts = () => {
    let total = 0;

    // Base cost
    let baseCost = 0;
    if (state.includeBase) {
      let row =
        state.partners <= 10
          ? 0
          : state.partners <= 30
          ? 1
          : state.partners <= 100
          ? 2
          : 3;
      baseCost = baseMatrix[row][state.importAmount - 1];
      total += baseCost;
    }

    // Reason cost
    let reasonCost = 0;
    if (state.includeReason) {
      reasonCost =
        state.reasonType === "basic"
          ? 100000
          : 100000 + state.missingItems * 30000;
      total += reasonCost;
    }

    // Doc cost
    let docCost = 0;
    if (state.includeDoc) {
      docCost = state.docPartners * 50000;
      total += docCost;
    }

    // Report cost
    let reportCost = 0;
    if (state.includeReport) {
      const checkedCount = Object.values(state.reportItems).filter(Boolean)
        .length;
      reportCost = checkedCount * 1000000;
      total += reportCost;
    }

    // Related cost
    let relatedCost = 0;
    if (state.includeRelated) {
      let row =
        state.relatedPartners <= 10
          ? 0
          : state.relatedPartners <= 30
          ? 1
          : state.relatedPartners <= 50
          ? 2
          : 3;
      relatedCost = relatedMatrix[row][state.relatedImportAmount - 1];
      total += relatedCost;
    }

    setCosts({ baseCost, reasonCost, docCost, reportCost, relatedCost, totalCost: total });
  };

  return (
    <form
      onSubmit={(e) => {
        e.preventDefault();
        calculateCosts();
        alert(`Ï¥ù Í≤¨Ï†ÅÏùÄ ${costs.totalCost.toLocaleString()}Ïõê ÏûÖÎãàÎã§.`);
      }}
      style={{
        maxWidth: 900,
        margin: "0 auto",
        padding: 20,
        fontFamily: "Pretendard, 'Segoe UI', sans-serif",
      }}
    >
      <h1
        style={{
          textAlign: "center",
          color: "#1a2e59",
          marginBottom: 30,
        }}
      >
        Í≥ºÏÑ∏ÏûêÎ£å Ï†úÏ∂ú Ïª®ÏÑ§ÌåÖ Í≤¨Ï†ÅÏÑú ÏÉùÏÑ±Í∏∞
      </h1>

      <div
        style={{
          marginBottom: 30,
          maxWidth: 400,
          marginLeft: "auto",
          marginRight: "auto",
          display: "flex",
          flexDirection: "column",
        }}
      >
        <label
          style={{ fontWeight: 600, marginBottom: 10, color: "#1a2e59" }}
          htmlFor="company"
        >
          ÌöåÏÇ¨Î™Ö:
        </label>
        <input
          type="text"
          id="company"
          name="company"
          required
          placeholder="ÌöåÏÇ¨Î™ÖÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
          style={{
            padding: 12,
            fontSize: 15,
            borderRadius: 8,
            border: "2px solid #ced4da",
          }}
          onChange={handleChange}
          value={state.company || ""}
        />
      </div>

      <div
        style={{
          marginBottom: 30,
          maxWidth: 400,
          marginLeft: "auto",
          marginRight: "auto",
          display: "flex",
          flexDirection: "column",
        }}
      >
        <label
          style={{ fontWeight: 600, marginBottom: 10, color: "#1a2e59" }}
          htmlFor="doc_number"
        >
          Î¨∏ÏÑúÎ≤àÌò∏:
        </label>
        <input
          type="text"
          id="doc_number"
          name="doc_number"
          required
          placeholder="Ïà´Ïûê 3ÏûêÎ¶¨ ÏûÖÎ†•"
          style={{
            padding: 12,
            fontSize: 15,
            borderRadius: 8,
            border: "2px solid #ced4da",
          }}
          onChange={handleChange}
          value={state.doc_number || ""}
        />
      </div>

      <h2
        style={{
          textAlign: "center",
          margin: "50px 0 30px",
          fontSize: 22,
          fontWeight: 700,
          color: "#1a2e59",
          borderBottom: "3px solid #3f72af",
          paddingBottom: 10,
          maxWidth: 420,
          marginLeft: "auto",
          marginRight: "auto",
        }}
      >
        ÏÑúÎπÑÏä§ Ìï≠Î™© ÏÑ†ÌÉù
      </h2>

      <table
        style={{
          width: "100%",
          borderCollapse: "separate",
          borderSpacing: "0 12px",
          fontSize: 15,
          color: "#333",
          marginBottom: 50,
        }}
      >
        <thead>
          <tr
            style={{
              backgroundColor: "#3f72af",
              color: "white",
              fontWeight: 700,
              fontSize: 16,
              letterSpacing: "0.04em",
            }}
          >
            <th
              style={{
                borderRadius: "10px 0 0 10px",
                padding: "16px 20px",
                textAlign: "center",
