import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import jsPDF from "jspdf";
import html2canvas from "html2canvas";

export default function Preview() {
  const location = useLocation();
  const navigate = useNavigate();

  const formData = location.state?.formData || {};
  const costs = location.state?.costs || {};

  const today = new Date();
  const dateString = `${String(today.getFullYear()).slice(2)}${String(
    today.getMonth() + 1
  ).padStart(2, "0")}${String(today.getDate()).padStart(2, "0")}`;

  const downloadPDF = () => {
    const doc = new jsPDF("p", "pt", "a4");
    const input = document.getElementById("invoice-content");

    const companyNameSafe = formData.company
      ? formData.company.replace(/[^Í∞Ä-Ìû£a-zA-Z0-9\s]/g, "")
      : "ÏóÖÏ≤¥Î™ÖÏóÜÏùå";
    const filename = `[Í¥ÄÏÑ∏Î≤ïÏù∏ÌïúÏòÅ] ${companyNameSafe} Í≥ºÏÑ∏ÏûêÎ£å Ïª®ÏÑ§ÌåÖ Í≤¨Ï†ÅÏÑú_${dateString}.pdf`;

    html2canvas(input, { scale: 2 }).then((canvas) => {
      const imgData = canvas.toDataURL("image/png");
      const pdfWidth = doc.internal.pageSize.getWidth() - 40;
      const imgProps = doc.getImageProperties(imgData);
      const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;

      doc.addImage(imgData, "PNG", 20, 20, pdfWidth, pdfHeight);
      doc.save(filename);
    });
  };

  const importAmountMap = {
    1: "10Ïñµ ÎØ∏Îßå",
    2: "10Ïñµ Ïù¥ÏÉÅ ~ 50Ïñµ ÎØ∏Îßå",
    3: "50Ïñµ Ïù¥ÏÉÅ",
  };

  const reportItemLabels = {
    royalty: "Î°úÏó¥Ìã∞",
    productionSupport: "ÏÉùÏÇ∞ÏßÄÏõêÎπÑ",
    freightInsurance: "Ïö¥ÏûÑ/Î≥¥ÌóòÎ£å",
    commission: "Ïª§ÎØ∏ÏÖò",
    tools: "Í∏àÌòïÎπÑ/Î¨¥ÏÉÅÏ†úÍ≥µ",
    others: "Í∏∞ÌÉÄ",
  };

  const serviceItems = [
    {
      label: "Í±∞ÎûòÍµ¨Ï°∞ Î∞è Í≥ºÏÑ∏ÏûêÎ£å Ï†úÏ∂ú Î≤îÏúÑ Í≤ÄÌÜ†",
      checked: formData.includeBase,
      quantity: formData.partners || 1,
      total: costs.baseCost,
      details: `Ïó∞ ÏàòÏûÖÍ∏àÏï°: ${importAmountMap[formData.importAmount] || "ÎØ∏ÏûÖÎ†•"}`,
    },
    {
      label: "Í≥ºÏÑ∏ÏûêÎ£å ÎØ∏Ï†úÏ∂ú ÏÇ¨Ïú†ÏÑú ÏûëÏÑ± ÏßÄÏõê",
      checked: formData.includeReason,
      quantity: formData.reasonPartners || 1,
      total: costs.reasonCost,
      details:
        formData.reasonType === "basic"
          ? "Í±∞Îûò ÏÇ¨Ïã§ ÏóÜÏùå"
          : `ÎπÑÍ≥ºÏÑ∏ Í±∞Îûò (Ìï≠Î™© Ïàò: ${formData.missingItems || 0})`,
    },
    {
      label: "Í≥ºÏÑ∏ÏûêÎ£å Ï†úÏ∂ú ÏÑúÎ•ò Ï†ïÎπÑ Î∞è Ï†ÑÏûêÏ†úÏ∂ú ÎåÄÌñâ",
      checked: formData.includeDoc,
      quantity: formData.docPartners || 1,
      total: costs.docCost,
      details: ``,
    },
    {
      label: "Í≥ºÏÑ∏Í∞ÄÍ≤© Í≤∞Ï†ï ÏÇ¨Ïú† Î≥¥Í≥†ÏÑú ÏûëÏÑ±",
      checked: formData.includeReport,
      quantity:
        Object.values(formData.reportItems || {}).filter(Boolean).length *
        (formData.reportPartners || 1),
      total: costs.reportCost,
      details: `${
        Object.entries(formData.reportItems || {})
          .filter(([_, value]) => value)
          .map(([key]) => reportItemLabels[key] || key)
          .join(", ") || "ÏóÜÏùå"
      }`,
    },
    {
      label: "ÌäπÏàòÍ¥ÄÍ≥ÑÏûê Í±∞Îûò Î∂ÑÏÑù Î∞è Ïù¥Ï†ÑÍ∞ÄÍ≤© Í≤ÄÌÜ† Î¶¨Ìè¨Ìä∏",
      checked: formData.includeRelated,
      quantity: formData.relatedPartners || 1,
      total: costs.relatedCost,
      details: `Ïó∞ ÏàòÏûÖÍ∏àÏï°: ${importAmountMap[formData.relatedImportAmount] || "ÎØ∏ÏûÖÎ†•"}`,
    },
  ];

  const selectedServices = serviceItems.filter((item) => item.checked);

  return (
    <>


      <div
        id="invoice-content"
        style={{
          fontFamily: "'Pretendard', 'Helvetica Neue', sans-serif",
          backgroundColor: "#fff",
          color: "#222",
          padding: 60,
          maxWidth: 800,
          margin: "60px auto",
          lineHeight: 1.7,
          border: "1px solid #ddd",
          minHeight: "1122px", // A4 height (297mm at 96dpi)
          boxSizing: "border-box",
          display: "flex",
          flexDirection: "column",
          justifyContent: "space-between"
        }}
      >
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 60 }}>
  <div>
    <h1 style={{ fontSize: 40, fontWeight: 700, marginBottom: 10 }}>Invoice</h1>
    <p style={{ fontSize: 14, color: '#555' }}>No. {formData.doc_number || 'XXXX'} &nbsp; / &nbsp; {today.toLocaleDateString('ko-KR')}</p>
  </div>
  <div style={{ width: 170, height: 'auto' }}>
    <img
      src={`${process.env.PUBLIC_URL}/images/HYLOGO_NAVY.png`}
      alt="ÌïúÏòÅ Î°úÍ≥†"
      style={{ width: '100%', height: 'auto', objectFit: 'contain' }}
    />
  </div>
</div>





        <div style={{ display: "flex", justifyContent: "space-between", marginBottom: 40 }}>
          <div>
            <strong>Client</strong>
            <p style={{ fontSize: 14 }}>{formData.company || "Client Name"}</p>
          </div>
          <div style={{ textAlign: "right" }}>
            <strong>Issued By</strong>
            <p style={{ fontSize: 14 }}>Í¥ÄÏÑ∏Î≤ïÏù∏ ÌïúÏòÅ</p>
          </div>
        </div>

        <table style={{ width: "100%", borderCollapse: "collapse", marginBottom: 40, fontSize: 14 }}>
          <thead>
            <tr style={{ borderBottom: "1px solid #ccc", backgroundColor: "#f9f9f9" }}>
              <th style={{ padding: "12px 8px", textAlign: "left" }}>Description</th>
              <th style={{ padding: "12px 8px", textAlign: "left" }}>Details</th>
              <th style={{ padding: "12px 8px", textAlign: "center" }}>Overseas Partners</th>
              <th style={{ padding: "12px 8px", textAlign: "right" }}>Rate (‚Ç©)</th>
            </tr>
          </thead>
          <tbody>
            {selectedServices.map((item, idx) => (
              <tr key={idx} style={{ borderBottom: "1px solid #eee" }}>
                <td style={{ padding: "12px 8px" }}>{item.label}</td>
                <td style={{ padding: "12px 8px" }}>{item.details}</td>
                <td style={{ padding: "12px 8px", textAlign: "center" }}>{item.quantity}</td>
                <td style={{ padding: "12px 8px", textAlign: "right" }}>{item.total.toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>

        <div style={{ display: "flex", justifyContent: "space-between", fontSize: 14, marginBottom: 40 }}>
          <div>
            <strong>Note</strong>
            <p style={{ whiteSpace: "pre-line" }}>{formData.note?.trim() || ""}</p>
          </div>

 <div style={{ textAlign: "right" }}>
    {Number(formData.discountRate) > 0 && (
      <p>Discount: {formData.discountRate}% ({costs.discountAmount?.toLocaleString()} Ïõê)</p>
    )}
    <p>VAT (10%): {costs.vat.toLocaleString()} Ïõê</p>
    <h3 style={{ fontSize: 20, marginTop: 10 }}>Total: {costs.totalCost.toLocaleString()} Ïõê</h3>
  </div>
        </div>

        <div style={{ display: "flex", justifyContent: "space-between", fontSize: 14 }}>
          <div>
            <p><strong>Bank:</strong> Ïã†ÌïúÏùÄÌñâ</p>
            <p><strong>Account:</strong> 140-015-193246</p>
            <p><strong>Holder:</strong> Í¥ÄÏÑ∏Î≤ïÏù∏ ÌïúÏòÅ Ïù∏Ï≤úÏßÄÏÇ¨</p>
          </div>
          <div style={{ textAlign: "right", fontSize: 13, color: "#666" }}>
            <p><strong>Valid Until:</strong> {new Date(today.getTime() + 15 * 86400000).toLocaleDateString("ko-KR")}</p>
            <p>Ïù∏Ï≤úÍ¥ëÏó≠Ïãú ÏÑúÍµ¨ Ïù¥Ïùå4Î°ú6 KRÎ≤ïÏ°∞ÌÉÄÏõå 915Ìò∏</p>
            <p>TEL: 032-713-4897 / FAX: 032-713-4898</p>
          </div>
        </div>
      </div>

      <div style={{ marginTop: 40, display: "flex", justifyContent: "center", gap: 20 }}>
        <button onClick={() => navigate("/")} style={{ backgroundColor: "#aaa", border: "none", padding: "10px 30px", borderRadius: 6, cursor: "pointer", fontWeight: "600", color: "#fff" }}>Îã§ÏãúÌïòÍ∏∞</button>
        <button onClick={downloadPDF} style={{ backgroundColor: "#111", border: "none", padding: "10px 30px", borderRadius: 6, cursor: "pointer", fontWeight: "700", color: "#fff" }}>üì• PDF Îã§Ïö¥Î°úÎìú</button>
      </div>
    </>
  );
}
